import React from 'react';
import Modal from './Modal';
import { useLanguage } from '../../contexts/LanguageContext';

const RAEventsModal = ({ isOpen, onClose, artistName }) => {
  const { t } = useLanguage();

  // Sample upcoming events data (in real app, this would be fetched from RA API)
  const upcomingEvents = [
    {
      id: 1,
      date: '2024-02-10',
      day: 'SAT',
      venue: 'Fabric London',
      city: 'London, UK',
      eventName: 'FUSE presents',
      time: '23:00 - 06:00',
      ticketStatus: 'On Sale'
    },
    {
      id: 2,
      date: '2024-02-17',
      day: 'SAT',
      venue: 'Berghain',
      city: 'Berlin, Germany',
      eventName: 'Klubnacht',
      time: '23:59 - 12:00',
      ticketStatus: 'Few Left'
    },
    {
      id: 3,
      date: '2024-02-24',
      day: 'SAT',
      venue: 'Womb',
      city: 'Tokyo, Japan',
      eventName: 'Techno Invasion',
      time: '22:00 - 05:00',
      ticketStatus: 'On Sale'
    },
    {
      id: 4,
      date: '2024-03-02',
      day: 'SAT',
      venue: 'Amnesia',
      city: 'Ibiza, Spain',
      eventName: 'Opening Party',
      time: '23:00 - 06:00',
      ticketStatus: 'Coming Soon'
    },
    {
      id: 5,
      date: '2024-03-09',
      day: 'SAT',
      venue: 'Printworks',
      city: 'London, UK',
      eventName: 'Drumcode',
      time: '14:00 - 23:00',
      ticketStatus: 'Sold Out'
    }
  ];

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const month = date.toLocaleDateString('en-US', { month: 'short' }).toUpperCase();
    const day = date.getDate();
    return { month, day };
  };

  const getTicketStatusClass = (status) => {
    switch(status) {
      case 'Sold Out': return 'status-sold-out';
      case 'Few Left': return 'status-few-left';
      case 'Coming Soon': return 'status-coming-soon';
      default: return 'status-on-sale';
    }
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={`${artistName} - ${t('ra.upcomingEvents')}`}
      className="ra-events-modal"
    >
      <div className="ra-events-container">
        <div className="ra-events-header">
          <p className="ra-events-subtitle">{t('ra.sourceRA')}</p>
          <a 
            href={`https://ra.co/dj/${artistName?.toLowerCase().replace(' ', '')}`}
            target="_blank"
            rel="noopener noreferrer"
            className="ra-view-all-link"
          >
            {t('ra.viewOnRA')} →
          </a>
        </div>

        <div className="ra-events-list">
          {upcomingEvents.length > 0 ? (
            upcomingEvents.map(event => {
              const { month, day } = formatDate(event.date);
              return (
                <div key={event.id} className="ra-event-card">
                  <div className="event-date-block">
                    <span className="event-month">{month}</span>
                    <span className="event-day">{day}</span>
                    <span className="event-weekday">{event.day}</span>
                  </div>

                  <div className="event-details">
                    <div className="event-header">
                      <h3 className="event-name">{event.eventName}</h3>
                      <span className={`ticket-status ${getTicketStatusClass(event.ticketStatus)}`}>
                        {event.ticketStatus}
                      </span>
                    </div>
                    <p className="event-venue">{event.venue}</p>
                    <div className="event-meta">
                      <span className="event-city">{event.city}</span>
                      <span className="event-time">{event.time}</span>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="no-events">
              <p>{t('ra.noUpcomingEvents')}</p>
            </div>
          )}
        </div>

        <div className="ra-events-footer">
          <p className="ra-disclaimer">{t('ra.disclaimer')}</p>
        </div>
      </div>
    </Modal>
  );
};

export default RAEventsModal;
