import React, { useState, useEffect } from 'react';
import { dummyProfiles, zones, countriesByZone, citiesByCountry, genresList } from '../../data/profiles';
import { HeartIcon, FilterIcon } from '../../utils/icons';
import InteractiveMap from '../common/InteractiveMap';
import ViewProfileScreen from './ViewProfileScreen';
import Modal from '../common/Modal';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';

const SearchScreen = () => {
  const { likedProfiles, toggleLike, connectionRequests, sendConnectionRequest } = useAppContext();
  const { t } = useLanguage();
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    roles: [],
    zones: [],
    countries: [],
    cities: [],
    genres: []
  });
  const [searchResults, setSearchResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);
  const [viewingProfile, setViewingProfile] = useState(null);
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState(null);
  const [message, setMessage] = useState('');

  // Dropdown states
  const [openDropdown, setOpenDropdown] = useState(null);

  useEffect(() => {
    // Show all profiles initially
    setSearchResults(dummyProfiles);
  }, []);

  const handleSearch = () => {
    let results = dummyProfiles;

    // Filter by search query (name)
    if (searchQuery) {
      results = results.filter(profile => 
        profile.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by roles
    if (filters.roles.length > 0) {
      results = results.filter(profile => filters.roles.includes(profile.role));
    }

    // Filter by zones
    if (filters.zones.length > 0) {
      results = results.filter(profile => filters.zones.includes(profile.zone));
    }

    // Filter by countries
    if (filters.countries.length > 0) {
      results = results.filter(profile => filters.countries.includes(profile.country));
    }

    // Filter by cities
    if (filters.cities.length > 0) {
      results = results.filter(profile => filters.cities.includes(profile.city));
    }

    // Filter by genres
    if (filters.genres.length > 0) {
      results = results.filter(profile => 
        profile.genres && profile.genres.some(genre => filters.genres.includes(genre))
      );
    }

    setSearchResults(results);
    setHasSearched(true);
  };

  const toggleFilterItem = (filterType, value) => {
    const newFilters = { ...filters };
    const currentArray = newFilters[filterType];

    if (currentArray.includes(value)) {
      newFilters[filterType] = currentArray.filter(item => item !== value);
    } else {
      newFilters[filterType] = [...currentArray, value];
    }

    setFilters(newFilters);
  };

  const handleMapCountryClick = (countryName) => {
    // Toggle country in filters
    toggleFilterItem('countries', countryName);
    // Auto-apply search when clicking on map
    setTimeout(() => handleSearch(), 100);
  };

  const clearFilters = () => {
    setFilters({
      roles: [],
      zones: [],
      countries: [],
      cities: [],
      genres: []
    });
  };

  const getAvailableCountries = () => {
    if (filters.zones.length > 0) {
      return filters.zones.flatMap(zone => countriesByZone[zone] || []).sort();
    }
    return Object.values(countriesByZone).flat().sort();
  };

  const getAvailableCities = () => {
    if (filters.countries.length > 0) {
      return filters.countries.flatMap(country => citiesByCountry[country] || []).sort();
    }
    return Object.values(citiesByCountry).flat().sort();
  };

  const activeFilterCount = Object.values(filters).reduce((count, arr) => count + arr.length, 0);

  const handleLike = (profileId) => {
    toggleLike(profileId);
  };

  const handleConnect = (profile) => {
    if (!connectionRequests.has(profile.id)) {
      setSelectedProfile(profile);
      setShowMessageModal(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim() && selectedProfile) {
      sendConnectionRequest(selectedProfile.id, message);
      setShowMessageModal(false);
      setMessage('');
      setSelectedProfile(null);
    }
  };

  const handleProfileClick = (profileId) => {
    setViewingProfile(profileId);
  };

  return (
    <div className="screen active search-screen">
      <div className="search-header">
        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder={t('search.searchByName')}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            className="search-input"
          />
          <button 
            className="filter-toggle-btn"
            onClick={() => setShowFilters(!showFilters)}
          >
            <span className="filter-icon"><FilterIcon /></span> {t('search.filters')}
            {activeFilterCount > 0 && (
              <span className="filter-count">{activeFilterCount}</span>
            )}
          </button>
        </div>

        {/* Interactive Map */}
        <div className="map-section">
          <InteractiveMap 
            onCountryClick={handleMapCountryClick}
            selectedCountries={filters.countries}
          />
        </div>

      </div>

      {/* Search Results */}
      <div className="search-results">
        {searchResults.length > 0 ? (
          searchResults.map(profile => {
            const isLiked = likedProfiles.has(profile.id);
            const isRequested = connectionRequests.has(profile.id);

            return (
              <div key={profile.id} className="search-result-card">
                <div className="result-content">
                  <div 
                    className={`result-avatar avatar-${profile.role.toLowerCase()} clickable`}
                    onClick={() => handleProfileClick(profile.id)}
                  >
                    {profile.avatar ? (
                      <img src={profile.avatar} alt={profile.name} />
                    ) : (
                      profile.name.charAt(0).toUpperCase()
                    )}
                    {profile.isVerified && <span className="verified-badge">✓</span>}
                  </div>
                  <div 
                    className="result-info clickable"
                    onClick={() => handleProfileClick(profile.id)}
                  >
                    <div className="result-header">
                      <h3>{profile.name}</h3>
                      <span className={`role-badge ${profile.role.toLowerCase()}`}>
                        {profile.role}
                      </span>
                    </div>
                    <p className="result-location">{profile.location}</p>
                  </div>
                </div>
                <div className="result-actions">
                  <button 
                    className={`btn ${isLiked ? 'btn-liked' : 'btn-outline'} btn-like`}
                    onClick={() => handleLike(profile.id)}
                  >
                    <HeartIcon /> {isLiked ? t('search.liked') : t('search.like')}
                  </button>
                  <button 
                    className={`btn ${isRequested ? 'btn-disabled' : 'btn-primary'} btn-connect`}
                    onClick={() => handleConnect(profile)}
                    disabled={isRequested}
                  >
                    {isRequested ? t('search.requested') : t('search.connect')}
                  </button>
                </div>
              </div>
            );
          })
        ) : (
          <div className="empty-state">
            <p>{hasSearched ? 'No results found' : 'Start searching to find profiles'}</p>
          </div>
        )}
      </div>

      {/* View Profile Modal */}
      {viewingProfile && (
        <ViewProfileScreen 
          profileId={viewingProfile}
          onClose={() => setViewingProfile(null)}
        />
      )}

      {/* Message Modal */}
      {showMessageModal && selectedProfile && (
        <div className="message-modal-overlay" onClick={() => {
          setShowMessageModal(false);
          setSelectedProfile(null);
          setMessage('');
        }}>
          <div className="message-modal-bottom" onClick={(e) => e.stopPropagation()}>
            <h2 className="message-modal-title">{t('search.sendMessageTo')} {selectedProfile.name}</h2>
            <textarea
              placeholder={t('messages.writeMessage')}
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              rows="5"
              className="message-textarea-bottom"
            />
            <div className="message-modal-actions">
              <button 
                className="btn btn-outline btn-modal-cancel"
                onClick={() => {
                  setShowMessageModal(false);
                  setSelectedProfile(null);
                  setMessage('');
                }}
              >
                {t('messages.cancel')}
              </button>
              <button 
                className="btn btn-primary btn-modal-send"
                onClick={handleSendMessage}
              >
                {t('messages.send')}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchScreen;
