import React, { useState, useRef } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';
import { genresList } from '../../data/profiles';
import { CloseIcon, UploadIcon } from '../../utils/icons';

const EditProfileScreen = ({ onClose }) => {
  const { user, updateUser } = useAppContext();
  const { t } = useLanguage();
  const [editedUser, setEditedUser] = useState({
    ...user,
    genres: user?.genres || []
  });
  const [selectedGenres, setSelectedGenres] = useState(new Set(user?.genres || []));
  const [showAllGenres, setShowAllGenres] = useState(false);
  const fileInputRef = useRef(null);

  const handleSave = () => {
    updateUser({
      ...editedUser,
      genres: Array.from(selectedGenres)
    });
    onClose();
  };

  const handleGenreToggle = (genre) => {
    const newGenres = new Set(selectedGenres);
    if (newGenres.has(genre)) {
      newGenres.delete(genre);
    } else {
      newGenres.add(genre);
    }
    setSelectedGenres(newGenres);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditedUser({ ...editedUser, avatar: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const displayedGenres = showAllGenres ? genresList : genresList.slice(0, 12);

  return (
    <div className="screen active edit-profile-screen">
      <div className="edit-profile-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>{t('profile.editProfile')}</h1>
        <button className="save-btn" onClick={handleSave}>
          Save
        </button>
      </div>

      <div className="edit-profile-content">
        {/* Profile Photo Section */}
        <div className="edit-section">
          <div className="profile-photo-upload">
            <div className="current-avatar">
              {editedUser.avatar ? (
                <img src={editedUser.avatar} alt={editedUser.name} />
              ) : (
                <div className="avatar-placeholder">
                  {editedUser.name?.charAt(0).toUpperCase()}
                </div>
              )}
            </div>
            <button 
              className="upload-btn"
              onClick={() => fileInputRef.current?.click()}
            >
              <UploadIcon />
              <span>Change Photo</span>
            </button>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              style={{ display: 'none' }}
            />
          </div>
        </div>

        {/* Basic Info Section */}
        <div className="edit-section">
          <h3>Basic Information</h3>

          <div className="form-group">
            <label>{t('profile.name')}</label>
            <input
              type="text"
              value={editedUser.name || ''}
              onChange={(e) => setEditedUser({ ...editedUser, name: e.target.value })}
              placeholder="Your name"
            />
          </div>

          <div className="form-group">
            <label>{t('profile.role')}</label>
            <select
              value={editedUser.role || ''}
              onChange={(e) => setEditedUser({ ...editedUser, role: e.target.value })}
            >
              <option value="ARTIST">Artist</option>
              <option value="VENUE">Venue</option>
              <option value="PROMOTER">Promoter</option>
              <option value="AGENT">Agent</option>
            </select>
          </div>

          <div className="form-group">
            <label>{t('profile.location')}</label>
            <input
              type="text"
              value={editedUser.location || ''}
              onChange={(e) => setEditedUser({ ...editedUser, location: e.target.value })}
              placeholder="City, Country"
            />
          </div>

          <div className="form-group">
            <label>{t('profile.bio')}</label>
            <textarea
              value={editedUser.bio || ''}
              onChange={(e) => setEditedUser({ ...editedUser, bio: e.target.value })}
              placeholder="Tell us about yourself..."
              rows="4"
            />
          </div>
        </div>

        {/* Genres Section */}
        <div className="edit-section">
          <h3>{t('profile.genres')}</h3>
          <div className="genres-grid">
            {displayedGenres.map(genre => (
              <label key={genre} className="genre-checkbox">
                <input
                  type="checkbox"
                  checked={selectedGenres.has(genre)}
                  onChange={() => handleGenreToggle(genre)}
                />
                <span className={selectedGenres.has(genre) ? 'selected' : ''}>
                  {genre}
                </span>
              </label>
            ))}
          </div>
          {genresList.length > 12 && (
            <button
              className="show-more-btn"
              onClick={() => setShowAllGenres(!showAllGenres)}
            >
              {showAllGenres ? 'Show less' : `Show all ${genresList.length} genres`}
            </button>
          )}
        </div>

        {/* Social Links Section */}
        <div className="edit-section">
          <h3>Social Links</h3>

          <div className="form-group">
            <label>Instagram</label>
            <input
              type="text"
              value={editedUser.instagram || ''}
              onChange={(e) => setEditedUser({ ...editedUser, instagram: e.target.value })}
              placeholder="@username"
            />
          </div>

          <div className="form-group">
            <label>Spotify Artist</label>
            <input
              type="url"
              value={editedUser.spotify || ''}
              onChange={(e) => setEditedUser({ ...editedUser, spotify: e.target.value })}
              placeholder="https://open.spotify.com/artist/..."
            />
          </div>

          <div className="form-group">
            <label>Resident Advisor</label>
            <input
              type="url"
              value={editedUser.residentAdvisor || ''}
              onChange={(e) => setEditedUser({ ...editedUser, residentAdvisor: e.target.value })}
              placeholder="https://ra.co/dj/..."
            />
          </div>

          <div className="form-group">
            <label>Website</label>
            <input
              type="url"
              value={editedUser.website || ''}
              onChange={(e) => setEditedUser({ ...editedUser, website: e.target.value })}
              placeholder="https://..."
            />
          </div>

          <div className="form-group">
            <label>SoundCloud/Mixtape</label>
            <input
              type="url"
              value={editedUser.mixtape || ''}
              onChange={(e) => setEditedUser({ ...editedUser, mixtape: e.target.value })}
              placeholder="https://soundcloud.com/..."
            />
          </div>
        </div>

        {/* Privacy Settings Section */}
        <div className="edit-section">
          <h3>Privacy Settings</h3>

          <div className="privacy-toggle">
            <label className="toggle-item">
              <input
                type="checkbox"
                checked={editedUser.calendarVisible}
                onChange={(e) => setEditedUser({ ...editedUser, calendarVisible: e.target.checked })}
              />
              <div className="toggle-info">
                <span className="toggle-label">Show Calendar to Others</span>
                <span className="toggle-description">
                  {user?.isPremium 
                    ? "Let others see when you're available"
                    : "Premium feature - Upgrade to enable"}
                </span>
              </div>
            </label>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="edit-actions">
          <button className="btn btn-secondary btn-full" onClick={onClose}>
            Cancel
          </button>
          <button className="btn btn-primary btn-full" onClick={handleSave}>
            Save Changes
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditProfileScreen;
