import React, { useState } from 'react';
import { CloseIcon, AddIcon } from '../../utils/icons';
import AddEventModal from '../common/AddEventModal';

const ArtistScheduleScreen = ({ artist, onClose }) => {
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [showAddEventModal, setShowAddEventModal] = useState(false);
  const [showEditEventModal, setShowEditEventModal] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);

  // Sample schedule data - now using state so we can modify it
  const [scheduleEvents, setScheduleEvents] = useState([
    {
      id: 1,
      date: '2024-12-31',
      venue: 'Womb Tokyo',
      city: 'Tokyo, Japan',
      status: 'confirmed',
      fee: '$3,500',
      time: '23:00 - 04:00'
    },
    {
      id: 2,
      date: '2025-01-15',
      venue: 'Fabric London',
      city: 'London, UK',
      status: 'pending',
      fee: '$4,200',
      time: '01:00 - 06:00'
    },
    {
      id: 3,
      date: '2025-01-28',
      venue: 'Berghain',
      city: 'Berlin, Germany',
      status: 'confirmed',
      fee: '$5,000',
      time: '02:00 - 08:00'
    },
    {
      id: 4,
      date: '2025-02-14',
      venue: 'Output',
      city: 'Brooklyn, NY',
      status: 'negotiating',
      fee: '$3,800',
      time: '22:00 - 03:00'
    }
  ]);

  // Event management functions
  const handleAddEvent = (newEvent) => {
    setScheduleEvents(prev => [...prev, newEvent]);
    // In real app, this would also save to backend
    console.log('New event added:', newEvent);
  };

  const handleEditEvent = (event) => {
    setEditingEvent(event);
    setShowEditEventModal(true);
  };

  const handleUpdateEvent = (updatedEvent) => {
    setScheduleEvents(prev =>
      prev.map(event => event.id === updatedEvent.id ? updatedEvent : event)
    );
    setEditingEvent(null);
    // In real app, this would also update in backend
    console.log('Event updated:', updatedEvent);
  };

  const handleDeleteEvent = (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      setScheduleEvents(prev => prev.filter(event => event.id !== eventId));
      // In real app, this would also delete from backend
      console.log('Event deleted:', eventId);
    }
  };

  const handleStatusChange = (eventId, newStatus) => {
    setScheduleEvents(prev =>
      prev.map(event =>
        event.id === eventId ? { ...event, status: newStatus } : event
      )
    );
    // In real app, this would also update in backend
    console.log('Event status changed:', eventId, newStatus);
  };

  const exportSchedule = () => {
    // Generate CSV or PDF export
    const csvData = scheduleEvents.map(event => ({
      Date: event.date,
      Venue: event.venue,
      Event: event.event,
      Fee: event.fee,
      Status: event.status,
      Time: event.time
    }));

    // Convert to CSV format
    const csvContent = [
      Object.keys(csvData[0]).join(','),
      ...csvData.map(row => Object.values(row).join(','))
    ].join('\n');

    // Create download
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${artist?.name}-schedule-${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);

    console.log('Schedule exported');
  };

  const sendAvailability = () => {
    // In real app, this would open email composer or send notification
    alert(`Availability for ${artist?.name} has been sent to venue contacts.`);
    console.log('Availability sent');
  };

  // Calculate dynamic stats
  const totalUpcoming = scheduleEvents.length;
  const totalRevenue = scheduleEvents.reduce((sum, event) => {
    const fee = parseInt(event.fee.replace(/[$,]/g, '')) || 0;
    return sum + fee;
  }, 0);
  const pendingCount = scheduleEvents.filter(event => event.status === 'pending').length;

  const getStatusBadge = (status) => {
    const statusClasses = {
      'confirmed': 'status-badge confirmed',
      'pending': 'status-badge pending',
      'negotiating': 'status-badge negotiating',
      'cancelled': 'status-badge cancelled'
    };
    return statusClasses[status] || 'status-badge';
  };

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  return (
    <div className="screen active artist-schedule-screen">
      <div className="schedule-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>{artist?.name} - Schedule</h1>
        <button className="add-event-btn" onClick={() => setShowAddEventModal(true)}>
          <AddIcon />
        </button>
      </div>

      <div className="schedule-content">
        {/* Date Filter */}
        <div className="schedule-filters">
          <select
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
            className="month-select"
          >
            {months.map((month, index) => (
              <option key={index} value={index}>{month}</option>
            ))}
          </select>
          <select
            value={selectedYear}
            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            className="year-select"
          >
            <option value={2024}>2024</option>
            <option value={2025}>2025</option>
            <option value={2026}>2026</option>
          </select>
        </div>

        {/* Schedule Overview - Dynamic Stats */}
        <div className="schedule-stats">
          <div className="stat-card">
            <h3>{totalUpcoming}</h3>
            <p>Upcoming Gigs</p>
          </div>
          <div className="stat-card">
            <h3>${totalRevenue.toLocaleString()}</h3>
            <p>Potential Revenue</p>
          </div>
          <div className="stat-card">
            <h3>{pendingCount}</h3>
            <p>Pending Bookings</p>
          </div>
        </div>

        {/* Event List */}
        <div className="schedule-events">
          <h3>Upcoming Events</h3>
          {scheduleEvents.map(event => (
            <div key={event.id} className="event-card">
              <div className="event-date">
                <span className="day">{new Date(event.date).getDate()}</span>
                <span className="month">{months[new Date(event.date).getMonth()].slice(0, 3)}</span>
              </div>
              <div className="event-info">
                <h4>{event.venue}</h4>
                <p className="event-location">{event.city}</p>
                <p className="event-time">{event.time}</p>
              </div>
              <div className="event-details">
                <select
                  value={event.status}
                  onChange={(e) => handleStatusChange(event.id, e.target.value)}
                  className={`status-select ${event.status}`}
                >
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="negotiating">Negotiating</option>
                  <option value="cancelled">Cancelled</option>
                </select>
                <p className="event-fee">{event.fee}</p>
              </div>
              <div className="event-actions">
                <button
                  className="btn btn-outline btn-sm"
                  onClick={() => handleEditEvent(event)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteEvent(event.id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="schedule-actions">
          <button className="btn btn-outline btn-full" onClick={exportSchedule}>
            📊 Export Schedule
          </button>
          <button className="btn btn-outline btn-full" onClick={sendAvailability}>
            📧 Send Availability
          </button>
          <button className="btn btn-primary btn-full" onClick={() => setShowAddEventModal(true)}>
            ➕ Add New Event
          </button>
        </div>
      </div>

      {/* Add Event Modal */}
      <AddEventModal
        artist={artist}
        isOpen={showAddEventModal}
        onClose={() => setShowAddEventModal(false)}
        onSave={handleAddEvent}
      />

      {/* Edit Event Modal */}
      <AddEventModal
        artist={artist}
        isOpen={showEditEventModal}
        onClose={() => {
          setShowEditEventModal(false);
          setEditingEvent(null);
        }}
        onSave={handleUpdateEvent}
        initialData={editingEvent}
        isEditing={true}
      />
    </div>
  );
};

export default ArtistScheduleScreen;
