import React, { useState } from 'react';
import { CloseIcon } from '../../utils/icons';
import ArtistScheduleScreen from './ArtistScheduleScreen';
import FinancialOverviewScreen from './FinancialOverviewScreen';
import PerformanceAnalyticsScreen from './PerformanceAnalyticsScreen';
import ContractDetailsScreen from './ContractDetailsScreen';
import MediaPressKitScreen from './MediaPressKitScreen';
import BookNewGigsScreen from './BookNewGigsScreen';

const ManageArtistScreen = ({ artist, onClose }) => {
  if (!artist) return null;

  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : 'A';
  };

  return (
    <div className="screen active manage-artist-screen">
      <div className="manage-artist-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>Manage {artist.name}</h1>
      </div>

      <div className="manage-artist-content">
        <div className="manage-artist-info">
          <div className="artist-avatar">
            {artist.avatar ? (
              <img src={artist.avatar} alt={artist.name} />
            ) : (
              getInitial(artist.name)
            )}
          </div>
          <div>
            <h4>{artist.name}</h4>
            <p>{artist.location}</p>
            {artist.genres && artist.genres.length > 0 && (
              <div className="artist-genres">
                {artist.genres.map(genre => (
                  <span key={genre} className="genre-tag-small">{genre}</span>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="manage-actions">
          <button className="btn btn-outline btn-full">
            📅 View Schedule
          </button>
          <button className="btn btn-outline btn-full">
            💰 Financial Overview
          </button>
          <button className="btn btn-outline btn-full">
            📊 Performance Analytics
          </button>
          <button className="btn btn-outline btn-full">
            📝 Contract Details
          </button>
          <button className="btn btn-outline btn-full">
            🎵 Media & Press Kit
          </button>
          <button className="btn btn-primary btn-full">
            🎯 Book New Gigs
          </button>
        </div>

        <div className="manage-artist-footer">
          <button
            className="btn btn-secondary btn-full"
            onClick={onClose}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default ManageArtistScreen;
