import React, { useState, useRef, useEffect } from 'react';
import { dummyProfiles } from '../../data/profiles';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';
import ViewProfileScreen from './ViewProfileScreen';

const ExploreScreen = () => {
  const { likedProfiles, toggleLike } = useAppContext();
  const { t } = useLanguage();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragPosition, setDragPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [viewingProfile, setViewingProfile] = useState(null);
  const cardRef = useRef(null);

  // Filter out already liked profiles from explore feed
  const exploreProfiles = dummyProfiles.filter(profile => profile.id !== 1); // Exclude current user (Al Jones)

  const currentProfile = exploreProfiles[currentIndex];

  // Find mutual likes (profiles that both users have liked)
  const getMutualLikes = (profile) => {
    // This is sample logic - in real app would check actual mutual likes
    const sampleMutualProfiles = dummyProfiles.slice(2, 5).map(p => p.name);
    return sampleMutualProfiles;
  };

  const truncateBio = (bio) => {
    if (!bio) return '';
    const words = bio.split(' ');
    const maxWords = 30;
    if (words.length <= maxWords) return bio;
    return words.slice(0, maxWords).join(' ') + '...';
  };

  const handleSwipe = (direction) => {
    if (!currentProfile) return;

    setSwipeDirection(direction);

    if (direction === 'right') {
      toggleLike(currentProfile.id);
    }

    // Move to next profile after animation
    setTimeout(() => {
      setCurrentIndex(prev => prev + 1);
      setSwipeDirection(null);
      setDragPosition({ x: 0, y: 0 });
    }, 300);
  };

  const handleLike = () => {
    handleSwipe('right');
  };

  const handleReject = () => {
    handleSwipe('left');
  };

  // Mouse drag handlers
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;

    const deltaX = e.clientX - dragStart.x;
    const deltaY = e.clientY - dragStart.y;
    setDragPosition({ x: deltaX, y: deltaY });
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const threshold = 100;
    if (dragPosition.x > threshold) {
      handleSwipe('right');
    } else if (dragPosition.x < -threshold) {
      handleSwipe('left');
    } else {
      setDragPosition({ x: 0, y: 0 });
    }
  };

  // Touch handlers for mobile
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;

    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    const deltaY = touch.clientY - dragStart.y;
    setDragPosition({ x: deltaX, y: deltaY });
  };

  const handleTouchEnd = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const threshold = 100;
    if (dragPosition.x > threshold) {
      handleSwipe('right');
    } else if (dragPosition.x < -threshold) {
      handleSwipe('left');
    } else {
      setDragPosition({ x: 0, y: 0 });
    }
  };

  useEffect(() => {
    if (!isDragging) {
      setDragPosition({ x: 0, y: 0 });
    }
  }, [isDragging]);

  const getCardStyle = () => {
    if (swipeDirection === 'left') {
      return {
        transform: 'translateX(-120%) rotate(-30deg)',
        opacity: 0,
        transition: 'all 0.3s ease-out'
      };
    }
    if (swipeDirection === 'right') {
      return {
        transform: 'translateX(120%) rotate(30deg)',
        opacity: 0,
        transition: 'all 0.3s ease-out'
      };
    }
    if (isDragging) {
      const rotation = dragPosition.x * 0.1;
      return {
        transform: `translate(${dragPosition.x}px, ${dragPosition.y}px) rotate(${rotation}deg)`,
        transition: 'none'
      };
    }
    return {
      transform: 'translate(0, 0) rotate(0)',
      transition: 'transform 0.2s ease-out'
    };
  };

  const getSwipeIndicatorOpacity = () => {
    const threshold = 100;
    if (dragPosition.x > 0) {
      return Math.min(dragPosition.x / threshold, 1);
    }
    return 0;
  };

  const getRejectIndicatorOpacity = () => {
    const threshold = 100;
    if (dragPosition.x < 0) {
      return Math.min(Math.abs(dragPosition.x) / threshold, 1);
    }
    return 0;
  };

  if (currentIndex >= exploreProfiles.length) {
    return (
      <div className="screen active explore-screen">
        <div className="explore-container">
          <div className="no-more-profiles">
            <h2>{t('explore.noMoreProfiles')}</h2>
            <p>{t('explore.checkBackLater')}</p>
          </div>
        </div>
      </div>
    );
  }

  if (!currentProfile) {
    return (
      <div className="screen active explore-screen">
        <div className="explore-container">
          <div className="loading-state">
            <p>{t('common.loading')}</p>
          </div>
        </div>
      </div>
    );
  }

  const mutualLikes = getMutualLikes(currentProfile);
  const isAlreadyLiked = likedProfiles.has(currentProfile.id);

  return (
    <div className="screen active explore-screen">
      <div className="explore-container">
        <div className="swipe-card-container">
          <div 
            ref={cardRef}
            className="swipe-card"
            style={getCardStyle()}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            onMouseLeave={handleMouseUp}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
          >
            {/* Like indicator */}
            <div 
              className="swipe-indicator like-indicator"
              style={{ opacity: getSwipeIndicatorOpacity() }}
            >
              LIKE
            </div>

            {/* Nope indicator */}
            <div 
              className="swipe-indicator nope-indicator"
              style={{ opacity: getRejectIndicatorOpacity() }}
            >
              NOPE
            </div>

            {/* Profile Image */}
            <div className="card-image-container">
              {currentProfile.avatar ? (
                <img 
                  src={currentProfile.avatar} 
                  alt={currentProfile.name}
                  className="card-image"
                  draggable={false}
                />
              ) : (
                <div className="card-image-placeholder">
                  {currentProfile.name.charAt(0).toUpperCase()}
                </div>
              )}
              {currentProfile.isVerified && (
                <span className="verified-badge">✓</span>
              )}
            </div>

            {/* Profile Info */}
            <div className="card-content">
              <div className="card-header">
                <h2>{currentProfile.name}</h2>
                <span className={`role-badge ${currentProfile.role.toLowerCase()}`}>
                  {currentProfile.role}
                </span>
              </div>

              <p className="card-location">{currentProfile.location}</p>

              {currentProfile.genres && currentProfile.genres.length > 0 && (
                <div className="card-genres">
                  {currentProfile.genres.slice(0, 3).map(genre => (
                    <span key={genre} className="genre-tag">{genre}</span>
                  ))}
                  {currentProfile.genres.length > 3 && (
                    <span className="genre-tag">+{currentProfile.genres.length - 3}</span>
                  )}
                </div>
              )}

              {mutualLikes.length > 0 && (
                <div className="mutual-likes">
                  <p className="mutual-likes-label">{t('explore.mutualLikes')}:</p>
                  <p className="mutual-likes-names">
                    {mutualLikes.join(', ')}
                  </p>
                </div>
              )}

              <div className="card-bio">
                <p>{truncateBio(currentProfile.bio)}</p>
              </div>

              <button 
                className="view-profile-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  setViewingProfile(currentProfile.id);
                }}
              >
                {t('explore.viewFullProfile')}
              </button>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="swipe-actions">
            <button 
              className="swipe-action-btn reject-btn"
              onClick={handleReject}
            >
              <svg width="30" height="30" viewBox="0 0 24 24" fill="none">
                <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" strokeWidth="3" strokeLinecap="round"/>
              </svg>
            </button>

            <button 
              className="swipe-action-btn like-btn"
              onClick={handleLike}
            >
              <svg width="30" height="30" viewBox="0 0 24 24" fill="none">
                <path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* View Profile Modal */}
      {viewingProfile && (
        <ViewProfileScreen 
          profileId={viewingProfile}
          onClose={() => setViewingProfile(null)}
        />
      )}
    </div>
  );
};

export default ExploreScreen;
