import React, { useState } from 'react';
import { CloseIcon } from '../../utils/icons';

const FinancialOverviewScreen = ({ artist, onClose }) => {
  const [selectedPeriod, setSelectedPeriod] = useState('ytd');

  // Sample financial data
  const financialData = {
    ytd: {
      totalEarnings: 45600,
      totalGigs: 12,
      avgGigFee: 3800,
      pendingPayments: 8200,
      expenses: 6800
    },
    monthly: {
      totalEarnings: 11400,
      totalGigs: 3,
      avgGigFee: 3800,
      pendingPayments: 7600,
      expenses: 1200
    }
  };

  const recentTransactions = [
    {
      id: 1,
      date: '2024-12-01',
      venue: 'Womb Tokyo',
      amount: 3500,
      status: 'paid',
      type: 'performance'
    },
    {
      id: 2,
      date: '2024-11-15',
      venue: 'Fabric London',
      amount: 4200,
      status: 'pending',
      type: 'performance'
    },
    {
      id: 3,
      date: '2024-11-08',
      venue: 'Travel - Tokyo to London',
      amount: -800,
      status: 'paid',
      type: 'expense'
    },
    {
      id: 4,
      date: '2024-10-28',
      venue: 'Berghain',
      amount: 5000,
      status: 'paid',
      type: 'performance'
    }
  ];

  const currentData = financialData[selectedPeriod];

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const getStatusBadge = (status) => {
    return status === 'paid' ? 'status-badge confirmed' : 'status-badge pending';
  };

  return (
    <div className="screen active financial-overview-screen">
      <div className="financial-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>{artist?.name} - Financials</h1>
      </div>

      <div className="financial-content">
        {/* Period Selector */}
        <div className="period-selector">
          <button
            className={`period-btn ${selectedPeriod === 'monthly' ? 'active' : ''}`}
            onClick={() => setSelectedPeriod('monthly')}
          >
            This Month
          </button>
          <button
            className={`period-btn ${selectedPeriod === 'ytd' ? 'active' : ''}`}
            onClick={() => setSelectedPeriod('ytd')}
          >
            Year to Date
          </button>
        </div>

        {/* Financial Overview Cards */}
        <div className="financial-stats">
          <div className="financial-card primary">
            <h3>{formatCurrency(currentData.totalEarnings)}</h3>
            <p>Total Earnings</p>
          </div>
          <div className="financial-card">
            <h3>{currentData.totalGigs}</h3>
            <p>Total Gigs</p>
          </div>
          <div className="financial-card">
            <h3>{formatCurrency(currentData.avgGigFee)}</h3>
            <p>Avg Gig Fee</p>
          </div>
          <div className="financial-card warning">
            <h3>{formatCurrency(currentData.pendingPayments)}</h3>
            <p>Pending Payments</p>
          </div>
        </div>

        {/* Net Profit Card */}
        <div className="profit-card">
          <h3>Net Profit</h3>
          <div className="profit-calculation">
            <div className="profit-line">
              <span>Earnings:</span>
              <span className="positive">{formatCurrency(currentData.totalEarnings)}</span>
            </div>
            <div className="profit-line">
              <span>Expenses:</span>
              <span className="negative">-{formatCurrency(currentData.expenses)}</span>
            </div>
            <div className="profit-line total">
              <span>Net Profit:</span>
              <span className="profit-amount">{formatCurrency(currentData.totalEarnings - currentData.expenses)}</span>
            </div>
          </div>
        </div>

        {/* Recent Transactions */}
        <div className="transactions-section">
          <h3>Recent Transactions</h3>
          <div className="transactions-list">
            {recentTransactions.map(transaction => (
              <div key={transaction.id} className="transaction-item">
                <div className="transaction-info">
                  <h4>{transaction.venue}</h4>
                  <p className="transaction-date">
                    {new Date(transaction.date).toLocaleDateString()}
                  </p>
                </div>
                <div className="transaction-details">
                  <div className={getStatusBadge(transaction.status)}>
                    {transaction.status}
                  </div>
                  <p className={`transaction-amount ${transaction.amount > 0 ? 'positive' : 'negative'}`}>
                    {transaction.amount > 0 ? '+' : ''}{formatCurrency(Math.abs(transaction.amount))}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Financial Actions */}
        <div className="financial-actions">
          <button className="btn btn-outline btn-full">
            ðŸ“Š Generate Report
          </button>
          <button className="btn btn-outline btn-full">
            ðŸ’¸ Send Invoice
          </button>
          <button className="btn btn-outline btn-full">
            ðŸ“ˆ Export Data
          </button>
          <button className="btn btn-primary btn-full">
            ðŸ’° Track Payment
          </button>
        </div>
      </div>
    </div>
  );
};

export default FinancialOverviewScreen;
