import React, { useState, useEffect } from 'react';
import { dummyProfiles, zones, countriesByZone, citiesByCountry, genresList } from '../../data/profiles';
import { HeartIcon } from '../../utils/icons';

const SearchScreen = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    roles: [],
    zones: [],
    countries: [],
    cities: [],
    genres: []
  });
  const [searchResults, setSearchResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  useEffect(() => {
    // Show all profiles initially
    setSearchResults(dummyProfiles);
  }, []);

  const handleSearch = () => {
    let results = dummyProfiles;

    // Filter by search query (name)
    if (searchQuery) {
      results = results.filter(profile => 
        profile.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by roles
    if (filters.roles.length > 0) {
      results = results.filter(profile => filters.roles.includes(profile.role));
    }

    // Filter by zones
    if (filters.zones.length > 0) {
      results = results.filter(profile => filters.zones.includes(profile.zone));
    }

    // Filter by countries
    if (filters.countries.length > 0) {
      results = results.filter(profile => filters.countries.includes(profile.country));
    }

    // Filter by cities
    if (filters.cities.length > 0) {
      results = results.filter(profile => filters.cities.includes(profile.city));
    }

    // Filter by genres
    if (filters.genres.length > 0) {
      results = results.filter(profile => 
        profile.genres && profile.genres.some(genre => filters.genres.includes(genre))
      );
    }

    setSearchResults(results);
    setHasSearched(true);
  };

  const toggleFilterItem = (filterType, value) => {
    const newFilters = { ...filters };
    const currentArray = newFilters[filterType];

    if (currentArray.includes(value)) {
      newFilters[filterType] = currentArray.filter(item => item !== value);
    } else {
      newFilters[filterType] = [...currentArray, value];
    }

    setFilters(newFilters);
  };

  const clearFilters = () => {
    setFilters({
      roles: [],
      zones: [],
      countries: [],
      cities: [],
      genres: []
    });
  };

  const getAvailableCountries = () => {
    if (filters.zones.length > 0) {
      return filters.zones.flatMap(zone => countriesByZone[zone] || []).sort();
    }
    return Object.values(countriesByZone).flat().sort();
  };

  const getAvailableCities = () => {
    if (filters.countries.length > 0) {
      return filters.countries.flatMap(country => citiesByCountry[country] || []).sort();
    }
    return Object.values(citiesByCountry).flat().sort();
  };

  const activeFilterCount = Object.values(filters).reduce((count, arr) => count + arr.length, 0);

  return (
    <div className="screen active search-screen">
      <div className="search-header">
        <h2>Search</h2>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by name..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            className="search-input"
          />
          <button 
            className="filter-toggle-btn"
            onClick={() => setShowFilters(!showFilters)}
          >
            <span className="filter-icon">⚙</span> Filters
            {activeFilterCount > 0 && (
              <span className="filter-count">{activeFilterCount}</span>
            )}
          </button>
        </div>

        {/* Filters Section */}
        {showFilters && (
          <div className="filters-section">
            <div className="filters-container">
              {/* Role Filter */}
              <div className="filter-group">
                <label>Roles</label>
                <div className="filter-options">
                  {['ARTIST', 'AGENT', 'PROMOTER', 'VENUE'].map(role => (
                    <button
                      key={role}
                      className={`filter-option ${filters.roles.includes(role) ? 'selected' : ''}`}
                      onClick={() => toggleFilterItem('roles', role)}
                    >
                      {role}
                    </button>
                  ))}
                </div>
              </div>

              {/* Zone Filter */}
              <div className="filter-group">
                <label>Zones</label>
                <div className="filter-options">
                  {zones.map(zone => (
                    <button
                      key={zone}
                      className={`filter-option ${filters.zones.includes(zone) ? 'selected' : ''}`}
                      onClick={() => toggleFilterItem('zones', zone)}
                    >
                      {zone}
                    </button>
                  ))}
                </div>
              </div>

              {/* Country Filter */}
              <div className="filter-group">
                <label>Countries</label>
                <div className="filter-options scrollable">
                  {getAvailableCountries().map(country => (
                    <button
                      key={country}
                      className={`filter-option ${filters.countries.includes(country) ? 'selected' : ''}`}
                      onClick={() => toggleFilterItem('countries', country)}
                    >
                      {country}
                    </button>
                  ))}
                </div>
              </div>

              {/* City Filter */}
              <div className="filter-group">
                <label>Cities</label>
                <div className="filter-options scrollable">
                  {getAvailableCities().map(city => (
                    <button
                      key={city}
                      className={`filter-option ${filters.cities.includes(city) ? 'selected' : ''}`}
                      onClick={() => toggleFilterItem('cities', city)}
                    >
                      {city}
                    </button>
                  ))}
                </div>
              </div>

              {/* Genre Filter */}
              <div className="filter-group">
                <label>Genres</label>
                <div className="filter-options scrollable">
                  {genresList.map(genre => (
                    <button
                      key={genre}
                      className={`filter-option ${filters.genres.includes(genre) ? 'selected' : ''}`}
                      onClick={() => toggleFilterItem('genres', genre)}
                    >
                      {genre}
                    </button>
                  ))}
                </div>
              </div>
            </div>

            <div className="filter-actions">
              <button className="btn btn-outline" onClick={clearFilters}>Clear Filters</button>
              <button className="btn btn-primary" onClick={handleSearch}>Apply Filters</button>
            </div>
          </div>
        )}
      </div>

      {/* Search Results */}
      <div className="search-results">
        {searchResults.length > 0 ? (
          searchResults.map(profile => (
            <div key={profile.id} className="search-result-card">
              <div className="result-avatar">
                <img src={profile.avatar} alt={profile.name} />
                {profile.isVerified && <span className="verified-badge">✓</span>}
              </div>
              <div className="result-info">
                <div className="result-header">
                  <h3>{profile.name}</h3>
                  <span className={`role-badge ${profile.role.toLowerCase()}`}>
                    {profile.role}
                  </span>
                </div>
                <p className="result-location">{profile.location}</p>
                {profile.genres && profile.genres.length > 0 && (
                  <div className="result-genres">
                    {profile.genres.slice(0, 3).map(genre => (
                      <span key={genre} className="genre-tag">{genre}</span>
                    ))}
                  </div>
                )}
              </div>
              <div className="result-actions">
                <button className="btn btn-outline btn-icon">
                  <HeartIcon />
                </button>
                <button className="btn btn-primary btn-connect">Connect</button>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-state">
            <p>{hasSearched ? 'No results found' : 'Start searching to find profiles'}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchScreen;
