import React, { useState } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';
import ViewProfileScreen from './ViewProfileScreen';

const MatchesScreen = () => {
  const { user, getCalendarMatches, connectionRequests, sendConnectionRequest } = useAppContext();
  const { t } = useLanguage();
  const [viewingProfile, setViewingProfile] = useState(null);
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState(null);
  const [message, setMessage] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [monthFilter, setMonthFilter] = useState('all');

  const allMatches = getCalendarMatches();

  // Filter matches based on selected filters
  const filteredMatches = allMatches.filter(match => {
    // Role filter
    if (roleFilter !== 'all' && match.profile.role !== roleFilter) {
      return false;
    }

    // Month filter
    if (monthFilter !== 'all') {
      // Extract month from dates string (e.g., "Jan 8-10, 2025" -> "Jan")
      const matchMonth = match.dates.split(' ')[0].toLowerCase();
      const filterMonth = monthFilter.toLowerCase();
      if (!matchMonth.includes(filterMonth.substring(0, 3))) {
        return false;
      }
    }

    return true;
  });

  const matches = filteredMatches;

  const handleConnect = (profile) => {
    if (!connectionRequests.has(profile.id)) {
      setSelectedProfile(profile);
      setShowMessageModal(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim() && selectedProfile) {
      sendConnectionRequest(selectedProfile.id, message);
      setShowMessageModal(false);
      setMessage('');
      setSelectedProfile(null);
    }
  };

  const handleProfileClick = (profileId) => {
    setViewingProfile(profileId);
  };

  // Show upgrade prompt for basic users
  if (!user?.isPremium) {
    return (
      <div className="screen active matches-screen">
        <div className="matches-header">
          <h1>Calendar Matches</h1>
        </div>
        <div className="upgrade-prompt">
          <div className="upgrade-card">
            <div className="upgrade-icon">✨</div>
            <h2>Unlock Calendar Matching</h2>
            <p>Get premium access to:</p>
            <ul>
              <li>📅 Find profiles with matching availability</li>
              <li>🌍 Search globally, not just locally</li>
              <li>🎯 See when artists are touring your city</li>
              <li>🔒 Control your calendar visibility</li>
            </ul>
            <button className="btn btn-premium">
              Upgrade to Premium
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="screen active matches-screen">
      <div className="matches-header">
        <h1>Calendar Matches</h1>
        <div className="premium-badge">
          <span>✨ Premium</span>
        </div>
      </div>

      {/* Filters Section */}
      <div className="matches-filters">
        <div className="filter-group">
          <label className="filter-label">Role</label>
          <select 
            className="filter-select"
            value={roleFilter}
            onChange={(e) => setRoleFilter(e.target.value)}
          >
            <option value="all">All Roles</option>
            <option value="VENUE">Venues</option>
            <option value="PROMOTER">Promoters</option>
            <option value="AGENT">Agents</option>
            <option value="ARTIST">Artists</option>
          </select>
        </div>

        <div className="filter-group">
          <label className="filter-label">Month</label>
          <select 
            className="filter-select"
            value={monthFilter}
            onChange={(e) => setMonthFilter(e.target.value)}
          >
            <option value="all">All Months</option>
            <option value="january">January</option>
            <option value="february">February</option>
            <option value="march">March</option>
            <option value="april">April</option>
            <option value="may">May</option>
            <option value="june">June</option>
            <option value="july">July</option>
            <option value="august">August</option>
            <option value="september">September</option>
            <option value="october">October</option>
            <option value="november">November</option>
            <option value="december">December</option>
          </select>
        </div>

        {(roleFilter !== 'all' || monthFilter !== 'all') && (
          <button 
            className="filter-clear-btn"
            onClick={() => {
              setRoleFilter('all');
              setMonthFilter('all');
            }}
          >
            Clear Filters
          </button>
        )}
      </div>

      {matches.length > 0 ? (
        <div className="matches-results">
          <p className="matches-count">
            {matches.length} {matches.length === 1 ? 'match' : 'matches'} found
          </p>

          {matches.map((match, index) => {
            const isRequested = connectionRequests.has(match.profile.id);

            return (
              <div key={`${match.profile.id}-${index}`} className="match-card">
                <div className="match-info">
                  <div className="match-type-badge">
                    {match.matchType === 'location' ? '📍' : '🎉'} 
                    {match.matchType === 'location' ? 'Location Match' : 'Event Match'}
                  </div>
                  <div className="match-dates">
                    📅 {match.dates} in {match.location}
                  </div>
                  <p className="match-reason">{match.reason}</p>
                </div>

                <div className="match-profile-card">
                  <div className="match-content">
                    <div 
                      className={`match-avatar avatar-${match.profile.role.toLowerCase()} clickable`}
                      onClick={() => handleProfileClick(match.profile.id)}
                    >
                      {match.profile.avatar ? (
                        <img src={match.profile.avatar} alt={match.profile.name} />
                      ) : (
                        match.profile.name.charAt(0).toUpperCase()
                      )}
                      {match.profile.isVerified && <span className="verified-badge">✓</span>}
                    </div>
                    <div 
                      className="match-info-content clickable"
                      onClick={() => handleProfileClick(match.profile.id)}
                    >
                      <div className="match-header">
                        <h3>{match.profile.name}</h3>
                        <span className={`role-badge ${match.profile.role.toLowerCase()}`}>
                          {match.profile.role}
                        </span>
                        {match.profile.isPremium && (
                          <span className="premium-indicator">✨</span>
                        )}
                      </div>
                      <p className="match-location">{match.profile.location}</p>
                      {match.profile.genres && (
                        <div className="match-genres">
                          {match.profile.genres.slice(0, 2).map(genre => (
                            <span key={genre} className="genre-tag-small">{genre}</span>
                          ))}
                          {match.profile.genres.length > 2 && (
                            <span className="genre-tag-small">+{match.profile.genres.length - 2}</span>
                          )}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="match-actions">
                    <button 
                      className={`btn ${isRequested ? 'btn-disabled' : 'btn-primary'} btn-match`}
                      onClick={() => handleConnect(match.profile)}
                      disabled={isRequested}
                    >
                      {isRequested ? 'Requested' : 'Connect'}
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="no-matches">
          <div className="no-matches-icon">📅</div>
          <h2>No Calendar Matches</h2>
          <p>No profiles match your travel schedule or events.</p>
          <div className="no-matches-tips">
            <h4>Tips to get more matches:</h4>
            <ul>
              <li>Add travel dates to your calendar</li>
              <li>Make sure your calendar is visible</li>
              <li>Check if there are profiles in your destinations</li>
            </ul>
          </div>
        </div>
      )}

      {/* View Profile Modal */}
      {viewingProfile && (
        <ViewProfileScreen 
          profileId={viewingProfile}
          onClose={() => setViewingProfile(null)}
        />
      )}

      {/* Message Modal */}
      {showMessageModal && selectedProfile && (
        <div className="message-modal-overlay" onClick={() => {
          setShowMessageModal(false);
          setSelectedProfile(null);
          setMessage('');
        }}>
          <div className="message-modal-bottom" onClick={(e) => e.stopPropagation()}>
            <h2 className="message-modal-title">Connect with {selectedProfile.name}</h2>
            <textarea
              placeholder="Introduce yourself and mention the calendar match..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              rows="4"
              className="message-textarea-bottom"
            />
            <div className="message-modal-actions">
              <button 
                className="btn btn-outline btn-modal-cancel"
                onClick={() => {
                  setShowMessageModal(false);
                  setSelectedProfile(null);
                  setMessage('');
                }}
              >
                Cancel
              </button>
              <button 
                className="btn btn-primary btn-modal-send"
                onClick={handleSendMessage}
              >
                Send Request
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MatchesScreen;
