import React, { useState, useRef, useEffect } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import { SendIcon, BackIcon } from '../../utils/icons';

const ChatScreen = ({ user, onClose }) => {
  const { messages, sendMessage } = useAppContext();
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const userMessages = messages[user.id] || [];

  useEffect(() => {
    scrollToBottom();
    inputRef.current?.focus();
  }, [userMessages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSend = () => {
    if (inputMessage.trim()) {
      sendMessage(user.id, inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="chat-screen active">
      <div className="chat-header">
        <button className="back-btn" onClick={onClose}>←</button>
        <div className="chat-user-info">
          <img src={user.avatar} alt={user.name} />
          <div>
            <h3>{user.name}</h3>
            <span className="status">{user.isOnline ? 'Online' : 'Offline'}</span>
          </div>
        </div>
        <button className="more-btn">⋮</button>
      </div>

      <div className="chat-messages">
        {userMessages.map((msg, index) => (
          <div 
            key={index} 
            className={`message ${msg.isMe ? 'message-sent' : 'message-received'}`}
          >
            <div className="message-bubble">
              <p>{msg.text}</p>
              <span className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString('en-US', { 
                  hour: 'numeric', 
                  minute: '2-digit' 
                })}
              </span>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-container">
        <div className="chat-input-wrapper">
          <button className="attach-btn">📎</button>
          <input
            ref={inputRef}
            type="text"
            placeholder="Type a message..."
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button 
            className="send-btn"
            onClick={handleSend}
            disabled={!inputMessage.trim()}
          >
            ➤
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatScreen;
