import React from 'react';
import { CloseIcon } from '../../utils/icons';

const ManageArtistScreen = ({ artist, onClose }) => {
  if (!artist) return null;

  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : 'A';
  };

  const handleContactArtist = () => {
    // Simple contact action - could open email or phone
    if (artist.email) {
      window.location.href = `mailto:${artist.email}?subject=Booking Inquiry - ${artist.name}`;
    } else {
      alert(`Contact ${artist.name} directly for booking inquiries.`);
    }
  };

  const handleViewContracts = () => {
    alert(`Viewing contracts for ${artist.name} - Feature coming soon!`);
  };

  const handleViewBookings = () => {
    alert(`Viewing upcoming bookings for ${artist.name} - Feature coming soon!`);
  };

  return (
    <div className="screen active manage-artist-screen">
      <div className="manage-artist-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>Manage {artist.name}</h1>
      </div>

      <div className="manage-artist-content">
        <div className="manage-artist-info">
          <div className="artist-avatar">
            {artist.avatar ? (
              <img src={artist.avatar} alt={artist.name} />
            ) : (
              getInitial(artist.name)
            )}
          </div>
          <div>
            <h4>{artist.name}</h4>
            <p>{artist.location}</p>
            {artist.genres && artist.genres.length > 0 && (
              <div className="artist-genres">
                {artist.genres.map(genre => (
                  <span key={genre} className="genre-tag-small">{genre}</span>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="manage-actions">
          <button
            className="btn btn-outline btn-full"
            onClick={() => handleScreenNavigation('schedule')}
          >
            📅 View Schedule
          </button>
          <button
            className="btn btn-outline btn-full"
            onClick={() => handleScreenNavigation('financial')}
          >
            💰 Financial Overview
          </button>
          <button
            className="btn btn-outline btn-full"
            onClick={() => handleScreenNavigation('analytics')}
          >
            📊 Performance Analytics
          </button>
          <button
            className="btn btn-outline btn-full"
            onClick={() => handleScreenNavigation('contracts')}
          >
            📝 Contract Details
          </button>
          <button
            className="btn btn-outline btn-full"
            onClick={() => handleScreenNavigation('media')}
          >
            🎵 Media & Press Kit
          </button>
          <button
            className="btn btn-primary btn-full"
            onClick={() => handleScreenNavigation('booking')}
          >
            🎯 Book New Gigs
          </button>
        </div>

        <div className="manage-artist-footer">
          <button
            className="btn btn-secondary btn-full"
            onClick={onClose}
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default ManageArtistScreen;
