import React, { useState } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import Modal from '../common/Modal';
import { LinkIcon, HeartIcon } from '../../utils/icons';
import { dummyProfiles } from '../../data/profiles';

const ViewProfileScreen = ({ profileId, onClose }) => {
  const { likedProfiles, toggleLike, connectionRequests, sendConnectionRequest } = useAppContext();
  const [showMessageModal, setShowMessageModal] = useState(false);
  const [message, setMessage] = useState('');

  // Find the profile from dummy data
  const profile = dummyProfiles.find(p => p.id === profileId);

  if (!profile) return null;

  const isLiked = likedProfiles.has(profileId);
  const isRequested = connectionRequests.has(profileId);

  const handleConnect = () => {
    if (!isRequested) {
      setShowMessageModal(true);
    }
  };

  const handleSendMessage = () => {
    if (message.trim()) {
      sendConnectionRequest(profileId, message);
      setShowMessageModal(false);
      setMessage('');
    }
  };

  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : 'A';
  };

  const getRoleBadgeClass = (role) => {
    const roleClasses = {
      'ARTIST': 'role-badge',
      'VENUE': 'role-badge venue',
      'PROMOTER': 'role-badge promoter',
      'AGENT': 'role-badge agent'
    };
    return roleClasses[role] || 'role-badge';
  };

  return (
    <Modal
      isOpen={true}
      onClose={onClose}
      title={profile.name}
      className="view-profile-modal"
    >
      <div className="profile-screen">
        <div className="profile-header">
          <div className="profile-avatar-container">
            <div className="profile-avatar">
              {profile.avatar ? (
                <img src={profile.avatar} alt={profile.name} />
              ) : (
                getInitial(profile.name)
              )}
            </div>
          </div>

          <h2 className="profile-name">{profile.name}</h2>
          <p className="profile-location">{profile.location}</p>
          <div className={getRoleBadgeClass(profile.role)}>
            {profile.role}
          </div>
          {profile.genres && profile.genres.length > 0 && (
            <div className="profile-genres-container">
              <div className="profile-genres">
                {profile.genres.map(genre => (
                  <span key={genre} className="genre-tag">{genre}</span>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Stats */}
        {(profile.followers || profile.connections) && (
          <div className="profile-stats">
            {profile.followers && (
              <div className="stat-item">
                <span className="stat-value">{profile.followers.toLocaleString()}</span>
                <span className="stat-label">Followers</span>
              </div>
            )}
            {profile.connections && (
              <div className="stat-item">
                <span className="stat-value">{profile.connections}</span>
                <span className="stat-label">Connections</span>
              </div>
            )}
            {profile.capacity && (
              <div className="stat-item">
                <span className="stat-value">{profile.capacity.toLocaleString()}</span>
                <span className="stat-label">Capacity</span>
              </div>
            )}
          </div>
        )}

        {/* Bio */}
        {profile.bio && (
          <div className="profile-bio">
            <p>{profile.bio}</p>
          </div>
        )}

        {/* Social Links */}
        {(profile.residentAdvisor || profile.spotify || profile.mixtape || profile.instagram || profile.website) && (
          <div className="social-links">
            {profile.residentAdvisor && (
              <a href={profile.residentAdvisor} target="_blank" rel="noopener noreferrer" className="social-link">
                <div className="social-link-content">
                  <LinkIcon />
                  <span>Resident Advisor</span>
                </div>
              </a>
            )}
            {profile.spotify && (
              <a href={profile.spotify} target="_blank" rel="noopener noreferrer" className="social-link">
                <div className="social-link-content">
                  <LinkIcon />
                  <span>Spotify</span>
                </div>
              </a>
            )}
            {profile.mixtape && (
              <a href={profile.mixtape} target="_blank" rel="noopener noreferrer" className="social-link">
                <div className="social-link-content">
                  <LinkIcon />
                  <span>Mixtape</span>
                </div>
              </a>
            )}
            {profile.instagram && (
              <a 
                href={`https://instagram.com/${profile.instagram.replace('@', '')}`} 
                target="_blank" 
                rel="noopener noreferrer"
                className="social-link"
              >
                <div className="social-link-content">
                  <LinkIcon />
                  <span>Instagram</span>
                </div>
              </a>
            )}
            {profile.website && (
              <a href={profile.website} target="_blank" rel="noopener noreferrer" className="social-link">
                <div className="social-link-content">
                  <LinkIcon />
                  <span>Website</span>
                </div>
              </a>
            )}
          </div>
        )}

        {/* Action Buttons */}
        <div className="profile-actions-bottom">
          <button 
            className={`btn ${isLiked ? 'btn-primary' : 'btn-outline'} btn-full-width`}
            onClick={() => toggleLike(profileId)}
          >
            <HeartIcon /> {isLiked ? 'Liked' : 'Like'}
          </button>
          <button 
            className={`btn ${isRequested ? 'btn-disabled' : 'btn-primary'} btn-full-width`}
            onClick={handleConnect}
            disabled={isRequested}
          >
            {isRequested ? 'Requested' : 'Connect'}
          </button>
        </div>

        {/* Message Modal */}
        {showMessageModal && (
          <div className="message-modal-overlay" onClick={() => setShowMessageModal(false)}>
            <div className="message-modal-bottom" onClick={(e) => e.stopPropagation()}>
              <h2 className="message-modal-title">Send Message to {profile.name}</h2>
              <textarea
                placeholder="Write your message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                rows="5"
                className="message-textarea-bottom"
              />
              <div className="message-modal-actions">
                <button 
                  className="btn btn-outline btn-modal-cancel"
                  onClick={() => setShowMessageModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="btn btn-primary btn-modal-send"
                  onClick={handleSendMessage}
                >
                  Send
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </Modal>
  );
};

export default ViewProfileScreen;
