import React, { useState, useEffect } from 'react';
import { dummyProfiles } from '../../data/profiles';

const InteractiveMap = ({ onCountryClick, selectedCountries = [], onProfileClick, searchResults = [] }) => {
  const [hoveredCountry, setHoveredCountry] = useState(null);
  const [hoveredProfile, setHoveredProfile] = useState(null);
  const [mapView, setMapView] = useState({ x: 0, y: 0, scale: 1 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [showProfileMarkers, setShowProfileMarkers] = useState(true);

  // Get profile markers based on search results or all profiles
  const getProfileMarkers = () => {
    const profilesToShow = searchResults.length > 0 ? searchResults : dummyProfiles;
    const profileCounts = {};

    return profilesToShow.map(profile => {
      const key = `${profile.city}-${profile.country}`;
      profileCounts[key] = (profileCounts[key] || 0) + 1;

      return {
        ...profile,
        locationKey: key,
        count: profileCounts[key]
      };
    });
  };

  // Major countries with their approximate positions on a world map (percentage-based)
  const countryMarkers = [
    // North America
    { name: 'United States', x: 20, y: 35, zone: 'North America' },
    { name: 'Canada', x: 20, y: 25, zone: 'North America' },
    { name: 'Mexico', x: 18, y: 45, zone: 'North America' },

    // South America
    { name: 'Brazil', x: 32, y: 65, zone: 'South America' },
    { name: 'Argentina', x: 28, y: 75, zone: 'South America' },
    { name: 'Colombia', x: 25, y: 55, zone: 'South America' },
    { name: 'Chile', x: 26, y: 72, zone: 'South America' },
    { name: 'Peru', x: 24, y: 62, zone: 'South America' },

    // Europe
    { name: 'United Kingdom', x: 48, y: 28, zone: 'Europe' },
    { name: 'Germany', x: 51, y: 30, zone: 'Europe' },
    { name: 'France', x: 49, y: 32, zone: 'Europe' },
    { name: 'Spain', x: 47, y: 35, zone: 'Europe' },
    { name: 'Italy', x: 52, y: 36, zone: 'Europe' },
    { name: 'Netherlands', x: 50, y: 29, zone: 'Europe' },
    { name: 'Belgium', x: 50, y: 30, zone: 'Europe' },
    { name: 'Poland', x: 54, y: 30, zone: 'Europe' },
    { name: 'Russia', x: 65, y: 25, zone: 'Europe' },
    { name: 'Greece', x: 55, y: 38, zone: 'Europe' },

    // Middle East
    { name: 'Turkey', x: 58, y: 38, zone: 'Middle East' },
    { name: 'Israel', x: 58, y: 42, zone: 'Middle East' },
    { name: 'UAE', x: 63, y: 45, zone: 'Middle East' },
    { name: 'Saudi Arabia', x: 60, y: 45, zone: 'Middle East' },

    // Africa
    { name: 'South Africa', x: 55, y: 75, zone: 'Africa' },
    { name: 'Egypt', x: 57, y: 42, zone: 'Africa' },
    { name: 'Morocco', x: 46, y: 40, zone: 'Africa' },
    { name: 'Nigeria', x: 50, y: 52, zone: 'Africa' },
    { name: 'Kenya', x: 59, y: 56, zone: 'Africa' },

    // Asia
    { name: 'Japan', x: 85, y: 36, zone: 'Asia' },
    { name: 'China', x: 75, y: 35, zone: 'Asia' },
    { name: 'South Korea', x: 82, y: 36, zone: 'Asia' },
    { name: 'Thailand', x: 73, y: 48, zone: 'Asia' },
    { name: 'Indonesia', x: 75, y: 58, zone: 'Asia' },
    { name: 'Singapore', x: 74, y: 54, zone: 'Asia' },
    { name: 'India', x: 68, y: 45, zone: 'Asia' },
    { name: 'Vietnam', x: 75, y: 48, zone: 'Asia' },
    { name: 'Philippines', x: 80, y: 50, zone: 'Asia' },

    // Oceania
    { name: 'Australia', x: 82, y: 70, zone: 'Oceania' },
    { name: 'New Zealand', x: 88, y: 78, zone: 'Oceania' }
  ];

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({
      x: e.clientX - mapView.x,
      y: e.clientY - mapView.y
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setMapView({
        ...mapView,
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleWheel = (e) => {
    e.preventDefault();
    const delta = e.deltaY < 0 ? 0.1 : -0.1;
    const newScale = Math.max(0.5, Math.min(3, mapView.scale + delta));
    setMapView({ ...mapView, scale: newScale });
  };

  const handleCountryClick = (country) => {
    if (onCountryClick) {
      onCountryClick(country.name);
    }
  };

  const handleProfileClick = (profile) => {
    if (onProfileClick) {
      onProfileClick(profile.id);
    }
  };

  const toggleProfileMarkers = () => {
    setShowProfileMarkers(!showProfileMarkers);
  };

  const resetView = () => {
    setMapView({ x: 0, y: 0, scale: 1 });
  };

  const zoomIn = () => {
    const newScale = Math.min(3, mapView.scale + 0.3);
    setMapView({ ...mapView, scale: newScale });
  };

  const zoomOut = () => {
    const newScale = Math.max(0.3, mapView.scale - 0.3);
    setMapView({ ...mapView, scale: newScale });
  };

  const getZoneColor = (zone) => {
    const colors = {
      'North America': '#FF6B6B',
      'South America': '#4ECDC4',
      'Europe': '#45B7D1',
      'Middle East': '#FFA07A',
      'Africa': '#98D8C8',
      'Asia': '#FFB6C1',
      'Oceania': '#DDA0DD'
    };
    return colors[zone] || '#888';
  };

  return (
    <div className="interactive-map-container">
      <div className="map-controls">
        <button onClick={zoomIn} className="map-control-btn" title="Zoom In">
          +
        </button>
        <button onClick={zoomOut} className="map-control-btn" title="Zoom Out">
          -
        </button>
        <button onClick={resetView} className="map-control-btn" title="Reset View">
          âŸ²
        </button>
        <button
          onClick={toggleProfileMarkers}
          className={`map-control-btn ${showProfileMarkers ? 'active' : ''}`}
          title="Toggle Profile Markers"
        >
          ðŸ‘¤
        </button>
      </div>

      <div 
        className="interactive-map"
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onWheel={handleWheel}
      >
        <div 
          className="map-content"
          style={{
            transform: `translate(${mapView.x}px, ${mapView.y}px) scale(${mapView.scale})`,
            cursor: isDragging ? 'grabbing' : 'grab'
          }}
        >
          {/* World map background */}
          <div className="world-map-bg">
            <img src="/world-map.svg" alt="World Map" className="map-image" />
          </div>

          {/* Country markers */}
          {countryMarkers.map((country) => {
            const isSelected = selectedCountries.includes(country.name);
            const isHovered = hoveredCountry === country.name;

            return (
              <div
                key={country.name}
                className={`country-marker ${isSelected ? 'selected' : ''} ${isHovered ? 'hovered' : ''}`}
                style={{
                  left: `${country.x}%`,
                  top: `${country.y}%`,
                  backgroundColor: isSelected ? '#FF3366' : getZoneColor(country.zone)
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  handleCountryClick(country);
                }}
                onMouseEnter={() => setHoveredCountry(country.name)}
                onMouseLeave={() => setHoveredCountry(null)}
              >
                <div className="country-tooltip">
                  <strong>{country.name}</strong>
                  <span>{country.zone}</span>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {hoveredCountry && (
        <div className="map-info">
          <span>Click to filter: {hoveredCountry}</span>
        </div>
      )}
    </div>
  );
};

export default InteractiveMap;
