import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import { useLanguage } from '../../contexts/LanguageContext';
import raService from '../../services/raService';

const RAEventsModal = ({ isOpen, onClose, artistName }) => {
  const { t } = useLanguage();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isOpen && artistName) {
      fetchEvents();
    }
  }, [isOpen, artistName]);

  const fetchEvents = async () => {
    setLoading(true);
    setError(null);

    try {
      const eventData = await raService.getArtistEvents(artistName);
      setEvents(eventData);
    } catch (err) {
      console.error('Failed to fetch RA events:', err);
      setError('Failed to load events. Please try again later.');
      setEvents([]);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const month = date.toLocaleDateString('en-US', { month: 'short' }).toUpperCase();
    const day = date.getDate();
    return { month, day };
  };

  const getTicketStatusClass = (status) => {
    switch(status) {
      case 'sold_out': return 'status-sold-out';
      case 'few_left': return 'status-few-left';
      case 'coming_soon': return 'status-coming-soon';
      case 'on_sale':
      default: return 'status-on-sale';
    }
  };

  const formatTicketStatus = (status) => {
    switch(status) {
      case 'sold_out': return 'Sold Out';
      case 'few_left': return 'Few Left';
      case 'coming_soon': return 'Coming Soon';
      case 'on_sale':
      default: return 'On Sale';
    }
  };

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={`${artistName} - ${t('ra.upcomingEvents')}`}
      className="ra-events-modal"
    >
      <div className="ra-events-container">
        <div className="ra-events-header">
          <p className="ra-events-subtitle">{t('ra.sourceRA')}</p>
          <a 
            href={`https://ra.co/dj/${artistName?.toLowerCase().replace(' ', '')}`}
            target="_blank"
            rel="noopener noreferrer"
            className="ra-view-all-link"
          >
            {t('ra.viewOnRA')} →
          </a>
        </div>

        <div className="ra-events-list">
          {loading ? (
            <div className="loading-events">
              <p>{t('common.loading')}</p>
            </div>
          ) : error ? (
            <div className="error-state">
              <p>{error}</p>
              <button onClick={fetchEvents} className="retry-btn">
                {t('common.retry')}
              </button>
            </div>
          ) : events.length > 0 ? (
            events.map(event => {
              const { month, day } = formatDate(event.date);
              return (
                <div key={event.id} className="ra-event-card">
                  <div className="event-date-block">
                    <span className="event-month">{month}</span>
                    <span className="event-day">{day}</span>
                    <span className="event-weekday">{event.dayOfWeek}</span>
                  </div>

                  <div className="event-details">
                    <div className="event-header">
                      <h3 className="event-name">{event.event.name}</h3>
                      <span className={`ticket-status ${getTicketStatusClass(event.tickets.status)}`}>
                        {formatTicketStatus(event.tickets.status)}
                      </span>
                    </div>
                    <p className="event-venue">{event.venue.name}</p>
                    <div className="event-meta">
                      <span className="event-city">{event.venue.city}, {event.venue.country}</span>
                      <span className="event-time">{event.event.startTime} - {event.event.endTime}</span>
                    </div>
                    {event.tickets.url && (
                      <a 
                        href={event.tickets.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="event-ticket-link"
                      >
                        {event.tickets.price} →
                      </a>
                    )}
                  </div>
                </div>
              );
            })
          ) : (
            <div className="no-events">
              <p>{t('ra.noUpcomingEvents')}</p>
            </div>
          )}
        </div>

        <div className="ra-events-footer">
          <p className="ra-disclaimer">{t('ra.disclaimer')}</p>
        </div>
      </div>
    </Modal>
  );
};

export default RAEventsModal;
