import React, { useState, useRef, useEffect } from 'react';
import { dummyProfiles } from '../../data/profiles';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';
import { CalendarIcon } from '../../utils/icons';
import Calendar from '../common/Calendar';

const ExploreScreen = () => {
  const { likedProfiles, toggleLike } = useAppContext();
  const { t } = useLanguage();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [swipeDirection, setSwipeDirection] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragPosition, setDragPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [isAudioMuted, setIsAudioMuted] = useState(true);
  const [showCalendar, setShowCalendar] = useState(false);
  const audioRef = useRef(null);
  const cardRef = useRef(null);

  // Filter out current user and put Aldonna first
  const otherProfiles = dummyProfiles.filter(profile => profile.id !== 1); // Exclude current user (Al Jones)
  const aldonnaProfile = otherProfiles.find(profile => profile.name === 'Aldonna');
  const remainingProfiles = otherProfiles.filter(profile => profile.name !== 'Aldonna');
  const exploreProfiles = aldonnaProfile ? [aldonnaProfile, ...remainingProfiles] : otherProfiles;

  const currentProfile = exploreProfiles[currentIndex];

  // Audio handling - Working demo
  useEffect(() => {
    if (currentProfile?.mixtape && currentProfile.role === 'ARTIST') {
      // Clean up previous audio
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.src = '';
        audioRef.current = null;
      }

      console.log(`ðŸŽµ Loading audio for: ${currentProfile.name}`);

      // Create audio element
      const audio = new Audio();
      audio.crossOrigin = 'anonymous';

      // For demo - use a royalty-free electronic music sample
      // In production, you'd need proper licensing for SoundCloud tracks
      audio.src = 'https://www.soundjay.com/misc/sounds-859.wav'; // Sample electronic beat

      // Set random start position
      const randomStart = 30 + Math.random() * 60; // 30s to 1.5min

      audio.addEventListener('loadedmetadata', () => {
        if (audio.duration > randomStart) {
          audio.currentTime = randomStart;
        }
      });

      // Set volume based on mute state
      audio.volume = isAudioMuted ? 0 : 0.2;
      audio.loop = true;

      audioRef.current = audio;

      // Try to autoplay (will be muted initially)
      audio.play().catch(err => {
        console.log('Autoplay prevented:', err);
        // Fallback: will play when user clicks unmute
      });

      return () => {
        if (audio) {
          audio.pause();
          audio.src = '';
        }
      };
    }
  }, [currentIndex, currentProfile]);

  // Handle mute/unmute
  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = isAudioMuted ? 0 : 0.2;
      if (!isAudioMuted && audioRef.current.paused) {
        audioRef.current.play().catch(err => console.log('Play failed:', err));
      }
    }
  }, [isAudioMuted]);

  // Find who I liked that also likes this profile
  const getLikedByConnections = (profile) => {
    // Get profiles that current user has liked
    const myLikedProfiles = Array.from(likedProfiles)
      .map(id => dummyProfiles.find(p => p.id === id))
      .filter(p => p);

    // For demo: assume some of my liked profiles also liked this profile
    // In real app, this would check actual data
    const likedByNames = myLikedProfiles.slice(0, 3).map(p => p.name);
    return likedByNames;
  };

  const truncateBio = (bio) => {
    if (!bio) return '';
    const words = bio.split(' ');
    const maxWords = 20;
    if (words.length <= maxWords) return bio;
    return words.slice(0, maxWords).join(' ') + '...';
  };

  const handleSwipe = (direction) => {
    if (!currentProfile) return;

    // Stop current audio
    if (audioRef.current) {
      audioRef.current.pause();
    }

    setSwipeDirection(direction);

    // Move to next profile after animation
    setTimeout(() => {
      setCurrentIndex(prev => prev + 1);
      setSwipeDirection(null);
      setDragPosition({ x: 0, y: 0 });
    }, 300);
  };

  // Mouse drag handlers
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX, y: e.clientY });
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;

    const deltaX = e.clientX - dragStart.x;
    const deltaY = e.clientY - dragStart.y;
    setDragPosition({ x: deltaX, y: deltaY });
  };

  const handleMouseUp = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const threshold = 100;
    if (dragPosition.x > threshold) {
      handleSwipe('right');
    } else if (dragPosition.x < -threshold) {
      handleSwipe('left');
    } else {
      setDragPosition({ x: 0, y: 0 });
    }
  };

  // Touch handlers for mobile
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setIsDragging(true);
    setDragStart({ x: touch.clientX, y: touch.clientY });
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;

    const touch = e.touches[0];
    const deltaX = touch.clientX - dragStart.x;
    const deltaY = touch.clientY - dragStart.y;
    setDragPosition({ x: deltaX, y: deltaY });
  };

  const handleTouchEnd = () => {
    if (!isDragging) return;
    setIsDragging(false);

    const threshold = 100;
    if (dragPosition.x > threshold) {
      handleSwipe('right');
    } else if (dragPosition.x < -threshold) {
      handleSwipe('left');
    } else {
      setDragPosition({ x: 0, y: 0 });
    }
  };

  useEffect(() => {
    if (!isDragging) {
      setDragPosition({ x: 0, y: 0 });
    }
  }, [isDragging]);

  const getCardStyle = () => {
    if (swipeDirection === 'left') {
      return {
        transform: 'translateX(-120%) rotate(-30deg)',
        opacity: 0,
        transition: 'all 0.3s ease-out'
      };
    }
    if (swipeDirection === 'right') {
      return {
        transform: 'translateX(120%) rotate(30deg)',
        opacity: 0,
        transition: 'all 0.3s ease-out'
      };
    }
    if (isDragging) {
      const rotation = dragPosition.x * 0.1;
      return {
        transform: `translate(${dragPosition.x}px, ${dragPosition.y}px) rotate(${rotation}deg)`,
        transition: 'none'
      };
    }
    return {
      transform: 'translate(0, 0) rotate(0)',
      transition: 'transform 0.2s ease-out'
    };
  };

  const getSwipeIndicatorOpacity = () => {
    const threshold = 100;
    if (dragPosition.x > 0) {
      return Math.min(dragPosition.x / threshold, 1);
    }
    return 0;
  };

  const getRejectIndicatorOpacity = () => {
    const threshold = 100;
    if (dragPosition.x < 0) {
      return Math.min(Math.abs(dragPosition.x) / threshold, 1);
    }
    return 0;
  };

  const toggleAudio = (e) => {
    e.stopPropagation();
    setIsAudioMuted(!isAudioMuted);
  };

  if (currentIndex >= exploreProfiles.length) {
    return (
      <div className="screen active explore-screen">
        <div className="explore-container">
          <div className="no-more-profiles">
            <h2>{t('explore.noMoreProfiles')}</h2>
            <p>{t('explore.checkBackLater')}</p>
          </div>
        </div>
      </div>
    );
  }

  if (!currentProfile) {
    return (
      <div className="screen active explore-screen">
        <div className="explore-container">
          <div className="loading-state">
            <p>{t('common.loading')}</p>
          </div>
        </div>
      </div>
    );
  }

  const likedByConnections = getLikedByConnections(currentProfile);
  const isAlreadyLiked = likedProfiles.has(currentProfile.id);

  return (
    <div className="screen active explore-screen">
      <div className="explore-container">
        <div 
          ref={cardRef}
          className="full-profile-page"
          style={getCardStyle()}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
            {/* Like indicator */}
            <div 
              className="swipe-indicator like-indicator"
              style={{ opacity: getSwipeIndicatorOpacity() }}
            >
              LIKE
            </div>

            {/* Nope indicator */}
            <div 
              className="swipe-indicator nope-indicator"
              style={{ opacity: getRejectIndicatorOpacity() }}
            >
              NOPE
            </div>

            {/* Audio Toggle Button - only for artists with mixtapes */}
            {currentProfile.role === 'ARTIST' && currentProfile.mixtape && (
              <button 
                className={`audio-toggle-btn ${isAudioMuted ? 'muted' : 'playing'}`}
                onClick={toggleAudio}
              >
                {isAudioMuted ? (
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M11 5L6 9H2v6h4l5 4V5zM23 9l-6 6M17 9l6 6"/>
                  </svg>
                ) : (
                  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M11 5L6 9H2v6h4l5 4V5z"/>
                    <path d="M19.07 4.93a10 10 0 0 1 0 14.14M15.54 8.46a5 5 0 0 1 0 7.07"/>
                  </svg>
                )}
              </button>
            )}

            {/* Full Profile Content */}
            <div className="full-profile-content">
              {/* Profile Header */}
              <div className="profile-header">
                <div className="profile-avatar-container">
                  <div className="profile-avatar">
                    {currentProfile.avatar ? (
                      <img src={currentProfile.avatar} alt={currentProfile.name} />
                    ) : (
                      currentProfile.name.charAt(0).toUpperCase()
                    )}
                  </div>
                </div>

                <h2 className="profile-name">{currentProfile.name}</h2>
                <p className="profile-location">{currentProfile.location}</p>
                <div className={`role-badge ${currentProfile.role.toLowerCase()}`}>
                  {currentProfile.role}
                </div>
                {currentProfile.genres && currentProfile.genres.length > 0 && (
                  <div className="profile-genres-container">
                    <div className="profile-genres expanded">
                      {currentProfile.genres.map(genre => (
                        <span key={genre} className="genre-tag">{genre}</span>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Liked By Section */}
              {likedByConnections.length > 0 && (
                <div className="liked-by-section">
                  <span className="liked-by-label">{t('explore.likedBy')}:</span>
                  <span className="liked-by-names">
                    {likedByConnections.join(', ')}
                  </span>
                </div>
              )}

              {/* Calendar CTA */}
              <div className="profile-actions">
                <button 
                  className="btn btn-outline btn-full-width"
                  onClick={() => setShowCalendar(true)}
                >
                  <CalendarIcon /> {t('profile.calendar')}
                </button>
              </div>


              {/* Bio Section */}
              {currentProfile.bio && (
                <div className="profile-bio">
                  <p>{currentProfile.bio}</p>
                </div>
              )}

              {/* Embedded Media Section */}
              <div className="profile-embeds">
                {currentProfile.mixtape && (
                  <div className="embed-card">
                    <h4>Latest Mix</h4>
                    <iframe 
                      src={`https://w.soundcloud.com/player/?url=${encodeURIComponent(currentProfile.mixtape)}&color=%23ff3366&auto_play=false&hide_related=true&show_comments=false&show_user=true&show_reposts=false&show_teaser=false&visual=true`}
                      frameBorder="0"
                      className="embed-iframe soundcloud-embed"
                      title="SoundCloud Mix"
                    />
                  </div>
                )}

                {currentProfile.spotify && (
                  <div className="embed-card">
                    <h4>Spotify Artist</h4>
                    <iframe 
                      src={`https://open.spotify.com/embed/artist/${currentProfile.spotify.split('/artist/')[1]?.split('?')[0]}`}
                      frameBorder="0"
                      allowTransparency="true"
                      allow="encrypted-media"
                      className="embed-iframe spotify-embed"
                      title="Spotify Artist Profile"
                    />
                  </div>
                )}

                {currentProfile.residentAdvisor && (
                  <div className="embed-card ra-card">
                    <h4>EVENTS</h4>
                    <a 
                      href={currentProfile.residentAdvisor}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="ra-profile-link"
                    >
                      View Events â†’
                    </a>
                  </div>
                )}
              </div>

              {/* Social CTAs */}
              <div className="profile-social-ctas">
                {currentProfile.instagram && (
                  <a 
                    href={`https://instagram.com/${currentProfile.instagram.replace('@', '')}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="btn btn-outline btn-social"
                  >
                    <span>Instagram</span>
                  </a>
                )}
                {currentProfile.website && (
                  <a
                    href={currentProfile.website}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn btn-outline btn-social"
                  >
                    <span>Website</span>
                  </a>
                )}
              </div>
            </div>
        </div>

        {/* Calendar Modal */}
        {showCalendar && (
          <Calendar onClose={() => setShowCalendar(false)} />
        )}
      </div>
    </div>
  );
};

export default ExploreScreen;
