import React, { useState } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import { HeartIcon } from '../../utils/icons';

const SearchScreen = () => {
  const { searchResults, searchUsers } = useAppContext();
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);

  const handleSearch = () => {
    searchUsers(searchQuery, {});
  };

  const nearbyUsers = [
    {
      id: 1,
      name: 'Simone De Kunovich',
      role: 'ARTIST',
      location: 'Milan, Italy',
      initial: 'S',
      color: '#6B5FFF'
    },
    {
      id: 2,
      name: 'Womb Tokyo',
      role: 'VENUE',
      location: 'Tokyo, Japan',
      initial: 'W',
      color: '#FF5757'
    }
  ];

  const [showMessageModal, setShowMessageModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [message, setMessage] = useState('');

  const handleConnect = (user) => {
    setSelectedUser(user);
    setShowMessageModal(true);
  };

  const sendMessage = () => {
    console.log('Sending message to', selectedUser?.name, ':', message);
    setShowMessageModal(false);
    setMessage('');
    setSelectedUser(null);
  };

  return (
    <div className="screen search-screen">
      <div className="search-header">
        <input
          type="text"
          className="search-input"
          placeholder="Search artists, venues, locations..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button 
          className="filter-btn"
          onClick={() => setShowFilters(!showFilters)}
        >
          Filters
        </button>
      </div>

      <div className="map-container">
        <div className="map-placeholder">
          <div className="map-dots">
            <div className="map-dot" style={{ top: '40%', left: '30%' }}>
              Osaka
            </div>
            <div className="map-dot" style={{ top: '50%', left: '50%' }}>
              Tokyo
            </div>
            <div className="map-dot" style={{ top: '35%', left: '70%' }}>
              Kyoto
            </div>
          </div>
        </div>
      </div>

      <div className="nearby-section">
        <h3 className="section-title">Nearby</h3>

        {nearbyUsers.map(user => (
          <div key={user.id} className="user-card">
            <div className="user-card-content">
              <div 
                className={`user-avatar-circle ${user.role === 'VENUE' ? 'venue' : ''}`}
                style={{ background: user.color }}
              >
                {user.initial}
              </div>
              <div className="user-info">
                <div className="user-name">
                  {user.name}
                  <span className={`user-role-badge ${user.role === 'VENUE' ? 'venue' : ''}`}>
                    {user.role}
                  </span>
                </div>
                <p className="user-location">{user.location}</p>
              </div>
            </div>
            <div className="user-actions">
              <button className="btn btn-outline btn-icon">
                <HeartIcon /> Like
              </button>
              <button 
                className="btn btn-primary"
                onClick={() => handleConnect(user)}
              >
                Connect
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Message Modal */}
      {showMessageModal && (
        <div className="modal active">
          <div className="modal-overlay" onClick={() => setShowMessageModal(false)} />
          <div className="modal-content">
            <div className="modal-header">
              <h3>Send Message to {selectedUser?.name}</h3>
              <button className="modal-close" onClick={() => setShowMessageModal(false)}>Ã—</button>
            </div>
            <div className="modal-body">
              <textarea
                className="message-input"
                placeholder="Write your message..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                rows="6"
                style={{
                  width: '100%',
                  padding: '14px',
                  background: '#000',
                  border: '1px solid #333',
                  borderRadius: '12px',
                  color: '#fff',
                  fontSize: '15px',
                  resize: 'none'
                }}
              />
              <div className="form-actions">
                <button 
                  className="btn btn-outline"
                  onClick={() => setShowMessageModal(false)}
                >
                  Cancel
                </button>
                <button 
                  className="btn btn-primary"
                  onClick={sendMessage}
                >
                  Send
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchScreen;
