import React, { useState, useRef } from 'react';
import { useAppContext } from '../../contexts/AppContext';
import { useLanguage } from '../../contexts/LanguageContext';
import Modal from '../common/Modal';
import RAEventsModal from '../common/RAEventsModal';
import { CalendarIcon, LinkIcon, UploadIcon, SwitchIcon } from '../../utils/icons';
import { genresList, dummyProfiles } from '../../data/profiles';
import Calendar from '../common/Calendar';

const ProfileScreen = () => {
  const { user, updateUser, userProfiles, currentProfileId, switchProfile } = useAppContext();
  const { t } = useLanguage();
  const [isEditing, setIsEditing] = useState(false);
  const [showCalendar, setShowCalendar] = useState(false);
  const [showLikesList, setShowLikesList] = useState(false);
  const [showConnectionsList, setShowConnectionsList] = useState(false);
  const [showGenreDropdown, setShowGenreDropdown] = useState(false);
  const [showAllGenres, setShowAllGenres] = useState(false);
  const [showRAEvents, setShowRAEvents] = useState(false);
  const [showProfileSwitcher, setShowProfileSwitcher] = useState(false);
  const fileInputRef = useRef(null);

  const [editForm, setEditForm] = useState({
    name: user?.name || 'Your Name',
    role: user?.role || 'ARTIST',
    bio: user?.bio || '',
    location: user?.location || 'Tokyo, Japan',
    city: user?.city || 'Tokyo',
    country: user?.country || 'Japan',
    genres: user?.genres || [],
    residentAdvisor: user?.residentAdvisor || '',
    mixtape: user?.mixtape || '',
    spotify: user?.spotify || '',
    instagram: user?.instagram || '',
    website: user?.website || '',
    spotifyTracks: user?.spotifyTracks || [],
    calendarVisible: user?.calendarVisible !== undefined ? user.calendarVisible : true
  });

  const [selectedGenres, setSelectedGenres] = useState(editForm.genres || []);

  const handleSave = () => {
    // Create location string from city and country
    const location = editForm.city && editForm.country 
      ? `${editForm.city}, ${editForm.country}`
      : editForm.city || editForm.country || 'Add Location';

    updateUser({ 
      ...editForm, 
      genres: selectedGenres,
      location: location 
    });
    setIsEditing(false);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        updateUser({ ...user, avatar: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const getInitial = (name) => {
    return name ? name.charAt(0).toUpperCase() : 'A';
  };

  const getRoleBadgeClass = (role) => {
    const roleClasses = {
      'ARTIST': 'role-badge',
      'VENUE': 'role-badge venue',
      'PROMOTER': 'role-badge promoter',
      'AGENT': 'role-badge agent'
    };
    return roleClasses[role] || 'role-badge';
  };

  const toggleGenre = (genre) => {
    if (selectedGenres.includes(genre)) {
      setSelectedGenres(selectedGenres.filter(g => g !== genre));
    } else {
      setSelectedGenres([...selectedGenres, genre]);
    }
  };

  // Sample liked profiles
  const likedProfiles = dummyProfiles.filter(p => p.role === 'VENUE').slice(0, 3);
  const connections = dummyProfiles.filter(p => p.role === 'ARTIST').slice(0, 5);

  return (
    <div className="screen active profile-screen">
      <div className="profile-header">
        <div className="profile-avatar-container">
          <div 
            className="profile-avatar clickable"
            onClick={() => fileInputRef.current?.click()}
          >
            {user?.avatar ? (
              <img src={user.avatar} alt={user?.name} />
            ) : (
              getInitial(user?.name)
            )}
            <div className="avatar-upload-overlay">
              <UploadIcon />
            </div>
          </div>
          <input
            ref={fileInputRef}
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            style={{ display: 'none' }}
          />
        </div>

        <h2 className="profile-name">{user?.name || t('profile.yourName')}</h2>
        <p className="profile-location">{user?.location || t('profile.addLocation')}</p>
        <div className={getRoleBadgeClass(user?.role)}>
          {user?.role || 'ARTIST'}
        </div>
        {user?.genres && user.genres.length > 0 && (
          <div className="profile-genres-container">
            <div className={`profile-genres ${showAllGenres ? 'expanded' : 'collapsed'}`}>
              {user.genres.map(genre => (
                <span key={genre} className="genre-tag">{genre}</span>
              ))}
            </div>
            {user.genres.length > 6 && (
              <button 
                className="see-more-btn"
                onClick={() => setShowAllGenres(!showAllGenres)}
              >
                {showAllGenres ? t('profile.seeLess') : t('profile.seeMore')}
              </button>
            )}
          </div>
        )}
      </div>

      <div className="profile-stats">
        <div className="stat-item" onClick={() => setShowLikesList(true)}>
          <span className="stat-value">12</span>
          <span className="stat-label">{t('profile.liked')}</span>
        </div>
        <div className="stat-item" onClick={() => setShowLikesList(true)}>
          <span className="stat-value">47</span>
          <span className="stat-label">{t('profile.likes')}</span>
        </div>
        <div className="stat-item" onClick={() => setShowConnectionsList(true)}>
          <span className="stat-value">23</span>
          <span className="stat-label">{t('profile.connections')}</span>
        </div>
      </div>


      <div className="profile-actions">
        <button 
          className="btn btn-primary btn-full-width"
          onClick={() => setIsEditing(true)}
        >
          {t('profile.editProfile')}
        </button>
        <button 
          className="btn btn-outline btn-full-width"
          onClick={() => setShowCalendar(true)}
        >
          <CalendarIcon /> {t('profile.calendar')}
        </button>
        <button 
          className="btn btn-secondary btn-full-width"
          onClick={() => setShowProfileSwitcher(true)}
        >
          <SwitchIcon /> Switch Profile
        </button>
      </div>

      {/* Bio Section */}
      {user?.bio && (
        <div className="profile-bio">
          <p>{user.bio}</p>
        </div>
      )}

      {/* Embedded Media Section */}
      <div className="profile-embeds">
        {user?.mixtape && (
          <div className="embed-card">
            <h4>Latest Mix</h4>
            <iframe 
              src={`https://w.soundcloud.com/player/?url=${encodeURIComponent(user.mixtape)}&color=%23ff3366&auto_play=false&hide_related=true&show_comments=false&show_user=true&show_reposts=false&show_teaser=false&visual=true`}
              frameBorder="0"
              className="embed-iframe soundcloud-embed"
              title="SoundCloud Mix"
            />
          </div>
        )}

        {user?.spotify && (
          <div className="embed-card">
            <h4>Spotify Artist</h4>
            <iframe 
              src={`https://open.spotify.com/embed/artist/${user.spotify.split('/artist/')[1]?.split('?')[0]}`}
              frameBorder="0"
              allowTransparency="true"
              allow="encrypted-media"
              className="embed-iframe spotify-embed"
              title="Spotify Artist Profile"
            />
          </div>
        )}

        {user?.residentAdvisor && (
          <div className="embed-card ra-card">
            <h4>Events</h4>
            <button 
              className="ra-events-button"
              onClick={() => setShowRAEvents(true)}
            >
              <span className="ra-icon">📅</span>
              <span>View Upcoming Events</span>
            </button>
            <a 
              href={user.residentAdvisor}
              target="_blank"
              rel="noopener noreferrer"
              className="ra-profile-link"
            >
              View Full RA Profile →
            </a>
          </div>
        )}
      </div>

      {/* Social CTAs */}
      <div className="profile-social-ctas">
        {user?.instagram && (
          <a 
            href={`https://instagram.com/${user.instagram.replace('@', '')}`} 
            target="_blank" 
            rel="noopener noreferrer"
            className="btn btn-outline btn-social"
          >
            <span>Instagram</span>
          </a>
        )}
        <button 
          className={`btn btn-outline btn-social ${!user?.website ? 'disabled' : ''}`}
          onClick={() => user?.website && window.open(user.website, '_blank')}
          disabled={!user?.website}
        >
          <span>Website</span>
        </button>
      </div>

      {/* Edit Profile Modal */}
      <Modal 
        isOpen={isEditing} 
        onClose={() => setIsEditing(false)}
        title={t('profile.editProfile')}
      >
        <div className="edit-form">
          <div className="form-group">
            <label>{t('editProfile.name')}</label>
            <input 
              type="text" 
              value={editForm.name}
              onChange={(e) => setEditForm({...editForm, name: e.target.value})}
              placeholder={t('editProfile.yourArtistName')}
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.role')}</label>
            <select 
              value={editForm.role}
              onChange={(e) => setEditForm({...editForm, role: e.target.value})}
            >
              <option value="ARTIST">Artist</option>
              <option value="AGENT">Agent</option>
              <option value="PROMOTER">Promoter</option>
              <option value="VENUE">Venue</option>
            </select>
          </div>

          <div className="form-group">
            <label>{t('editProfile.city')}</label>
            <input 
              type="text" 
              value={editForm.city}
              onChange={(e) => setEditForm({...editForm, city: e.target.value})}
              placeholder="City"
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.country')}</label>
            <input 
              type="text" 
              value={editForm.country}
              onChange={(e) => setEditForm({...editForm, country: e.target.value})}
              placeholder="Country"
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.genres')}</label>
            <div className="genre-selector">
              <div 
                className="genre-selector-header"
                onClick={() => setShowGenreDropdown(!showGenreDropdown)}
              >
                <span>
                  {selectedGenres.length > 0 
                    ? `${selectedGenres.length} ${t('editProfile.genresSelected')}`
                    : t('editProfile.selectGenres')
                  }
                </span>
                <span className="dropdown-arrow">{showGenreDropdown ? '▲' : '▼'}</span>
              </div>
              {showGenreDropdown && (
                <div className="genres-dropdown">
                  {genresList.map(genre => (
                    <label key={genre} className="genre-dropdown-item">
                      <input
                        type="checkbox"
                        checked={selectedGenres.includes(genre)}
                        onChange={() => toggleGenre(genre)}
                      />
                      <span className="genre-name">{genre}</span>
                    </label>
                  ))}
                </div>
              )}
            </div>
          </div>

          <div className="form-group">
            <label>{t('editProfile.bio')} ({t('editProfile.maxCharacters')})</label>
            <textarea 
              value={editForm.bio}
              onChange={(e) => {
                if (e.target.value.length <= 400) {
                  setEditForm({...editForm, bio: e.target.value});
                }
              }}
              rows="4"
              placeholder={t('editProfile.bioPlaceholder')}
            />
            <span className="char-count">{editForm.bio.length}/400</span>
          </div>

          <div className="form-group">
            <label>{t('editProfile.mixtapeLink')}</label>
            <input 
              type="url" 
              value={editForm.mixtape}
              onChange={(e) => setEditForm({...editForm, mixtape: e.target.value})}
              placeholder="https://soundcloud.com/yourname"
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.spotifyArtist')}</label>
            <input 
              type="url" 
              value={editForm.spotify}
              onChange={(e) => setEditForm({...editForm, spotify: e.target.value})}
              placeholder="https://open.spotify.com/artist/..."
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.residentAdvisor')}</label>
            <input 
              type="url" 
              value={editForm.residentAdvisor}
              onChange={(e) => setEditForm({...editForm, residentAdvisor: e.target.value})}
              placeholder="https://ra.co/dj/yourname"
            />
          </div>

          <div className="form-group">
            <label>{t('editProfile.instagram')}</label>
            <div className="input-with-prefix">
              <span className="input-prefix">@</span>
              <input 
                type="text" 
                value={editForm.instagram?.replace('@', '') || ''}
                onChange={(e) => {
                  const value = e.target.value.replace('@', '');
                  setEditForm({...editForm, instagram: value ? `@${value}` : ''});
                }}
                placeholder="username"
              />
            </div>
          </div>

          <div className="form-group">
            <label>{t('editProfile.website')}</label>
            <input 
              type="url" 
              value={editForm.website}
              onChange={(e) => setEditForm({...editForm, website: e.target.value})}
              placeholder="https://yourwebsite.com"
            />
          </div>

          {/* Calendar Visibility Settings - Premium Feature */}
          {user?.isPremium && (
            <div className="form-group">
              <label>{t('settings.calendarVisibility')}</label>
              <div className="privacy-setting">
                <label className="privacy-toggle">
                  <input
                    type="checkbox"
                    checked={editForm.calendarVisible}
                    onChange={(e) => setEditForm({...editForm, calendarVisible: e.target.checked})}
                  />
                  <span className="toggle-slider"></span>
                  <span className="toggle-label">{t('settings.showCalendar')}</span>
                </label>
                <p className="setting-note">{t('settings.calendarVisibilityNote')}</p>
              </div>
            </div>
          )}

          <div className="form-actions">
            <button 
              className="btn btn-outline"
              onClick={() => setIsEditing(false)}
            >
              {t('editProfile.cancel')}
            </button>
            <button 
              className="btn btn-primary"
              onClick={handleSave}
            >
              {t('editProfile.save')}
            </button>
          </div>
        </div>
      </Modal>

      {/* Calendar Modal */}
      {showCalendar && (
        <Calendar onClose={() => setShowCalendar(false)} />
      )}

      {/* Likes List Modal */}
      <Modal
        isOpen={showLikesList}
        onClose={() => setShowLikesList(false)}
        title={t('profile.likes')}
      >
        <div className="profiles-list">
          {likedProfiles.map(profile => (
            <div key={profile.id} className="profile-list-item">
              <img src={profile.avatar} alt={profile.name} />
              <div className="profile-info">
                <h4>{profile.name}</h4>
                <span className="profile-role">{profile.role}</span>
                <span className="profile-location">{profile.location}</span>
              </div>
            </div>
          ))}
        </div>
      </Modal>

      {/* Connections List Modal */}
      <Modal
        isOpen={showConnectionsList}
        onClose={() => setShowConnectionsList(false)}
        title={t('profile.connections')}
      >
        <div className="profiles-list">
          {connections.map(profile => (
            <div key={profile.id} className="profile-list-item">
              <img src={profile.avatar} alt={profile.name} />
              <div className="profile-info">
                <h4>{profile.name}</h4>
                <span className="profile-role">{profile.role}</span>
                <span className="profile-location">{profile.location}</span>
              </div>
            </div>
          ))}
        </div>
      </Modal>

      {/* Profile Switcher Modal */}
      <Modal
        isOpen={showProfileSwitcher}
        onClose={() => setShowProfileSwitcher(false)}
        title="Switch Profile"
      >
        <div className="profile-switcher-content">
          <p className="profile-switcher-description">
            Select which profile you want to manage:
          </p>
          <div className="profile-switcher-list">
            {userProfiles.map(profile => (
              <div
                key={profile.id}
                className={`profile-switcher-item ${profile.id === currentProfileId ? 'active' : ''}`}
                onClick={() => {
                  switchProfile(profile.id);
                  setShowProfileSwitcher(false);
                }}
              >
                <div className="switcher-avatar">
                  {profile.avatar ? (
                    <img src={profile.avatar} alt={profile.name} />
                  ) : (
                    profile.name.charAt(0).toUpperCase()
                  )}
                </div>
                <div className="switcher-info">
                  <h4>{profile.name}</h4>
                  <span className={`role-badge ${profile.role.toLowerCase()}`}>
                    {profile.role}
                  </span>
                  <p className="switcher-location">{profile.location}</p>
                </div>
                {profile.id === currentProfileId && (
                  <div className="active-indicator">✓</div>
                )}
              </div>
            ))}
          </div>
        </div>
      </Modal>

      {/* RA Events Modal */}
      <RAEventsModal
        isOpen={showRAEvents}
        onClose={() => setShowRAEvents(false)}
        artistName={user?.name}
      />
    </div>
  );
};

export default ProfileScreen;
