import React, { useState } from 'react';
import { CloseIcon, AddIcon } from '../../utils/icons';
import AddEventModal from '../common/AddEventModal';

const ArtistScheduleScreen = ({ artist, onClose }) => {
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());

  // Sample schedule data
  const scheduleEvents = [
    {
      id: 1,
      date: '2024-12-31',
      venue: 'Womb Tokyo',
      city: 'Tokyo, Japan',
      status: 'confirmed',
      fee: '$3,500',
      time: '23:00 - 04:00'
    },
    {
      id: 2,
      date: '2025-01-15',
      venue: 'Fabric London',
      city: 'London, UK',
      status: 'pending',
      fee: '$4,200',
      time: '01:00 - 06:00'
    },
    {
      id: 3,
      date: '2025-01-28',
      venue: 'Berghain',
      city: 'Berlin, Germany',
      status: 'confirmed',
      fee: '$5,000',
      time: '02:00 - 08:00'
    },
    {
      id: 4,
      date: '2025-02-14',
      venue: 'Output',
      city: 'Brooklyn, NY',
      status: 'negotiating',
      fee: '$3,800',
      time: '22:00 - 03:00'
    }
  ];

  const getStatusBadge = (status) => {
    const statusClasses = {
      'confirmed': 'status-badge confirmed',
      'pending': 'status-badge pending',
      'negotiating': 'status-badge negotiating',
      'cancelled': 'status-badge cancelled'
    };
    return statusClasses[status] || 'status-badge';
  };

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  return (
    <div className="screen active artist-schedule-screen">
      <div className="schedule-header">
        <button className="back-btn" onClick={onClose}>
          <CloseIcon />
        </button>
        <h1>{artist?.name} - Schedule</h1>
        <button className="add-event-btn">
          <AddIcon />
        </button>
      </div>

      <div className="schedule-content">
        {/* Date Filter */}
        <div className="schedule-filters">
          <select
            value={selectedMonth}
            onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
            className="month-select"
          >
            {months.map((month, index) => (
              <option key={index} value={index}>{month}</option>
            ))}
          </select>
          <select
            value={selectedYear}
            onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            className="year-select"
          >
            <option value={2024}>2024</option>
            <option value={2025}>2025</option>
            <option value={2026}>2026</option>
          </select>
        </div>

        {/* Schedule Overview */}
        <div className="schedule-stats">
          <div className="stat-card">
            <h3>4</h3>
            <p>Upcoming Gigs</p>
          </div>
          <div className="stat-card">
            <h3>$16,500</h3>
            <p>Potential Revenue</p>
          </div>
          <div className="stat-card">
            <h3>2</h3>
            <p>Pending Bookings</p>
          </div>
        </div>

        {/* Event List */}
        <div className="schedule-events">
          <h3>Upcoming Events</h3>
          {scheduleEvents.map(event => (
            <div key={event.id} className="event-card">
              <div className="event-date">
                <span className="day">{new Date(event.date).getDate()}</span>
                <span className="month">{months[new Date(event.date).getMonth()].slice(0, 3)}</span>
              </div>
              <div className="event-info">
                <h4>{event.venue}</h4>
                <p className="event-location">{event.city}</p>
                <p className="event-time">{event.time}</p>
              </div>
              <div className="event-details">
                <div className={getStatusBadge(event.status)}>
                  {event.status.charAt(0).toUpperCase() + event.status.slice(1)}
                </div>
                <p className="event-fee">{event.fee}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="schedule-actions">
          <button className="btn btn-outline btn-full">
            ðŸ“Š Export Schedule
          </button>
          <button className="btn btn-outline btn-full">
            ðŸ“§ Send Availability
          </button>
          <button className="btn btn-primary btn-full">
            âž• Add New Event
          </button>
        </div>
      </div>
    </div>
  );
};

export default ArtistScheduleScreen;
