import React, { useState } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import Header from './components/common/Header';
import TabBar from './components/common/TabBar';
import ProfileScreen from './components/screens/ProfileScreen';
import SearchScreen from './components/screens/SearchScreen';
import MatchesScreen from './components/screens/MatchesScreen';
import ExploreScreen from './components/screens/ExploreScreen';
import MessagesScreen from './components/screens/MessagesScreen';
import ChatScreen from './components/screens/ChatScreen';
import Modal from './components/common/Modal';
import { useLanguage } from './contexts/LanguageContext';
import './styles/App.css';
import './styles/responsive.css';

function App() {
  const [activeTab, setActiveTab] = useState('profile');
  const [activeChatUser, setActiveChatUser] = useState(null);
  const [showSettings, setShowSettings] = useState(false);
  const [showPremium, setShowPremium] = useState(false);
  const { t, language, changeLanguage, availableLanguages } = useLanguage();

  const renderScreen = () => {
    switch(activeTab) {
      case 'profile':
        return <ProfileScreen />;
      case 'search':
        return <SearchScreen />;
      case 'matches':
        return <MatchesScreen />;
      case 'explore':
        return <ExploreScreen />;
      case 'messages':
        return <MessagesScreen onOpenChat={setActiveChatUser} />;
      default:
        return <ProfileScreen />;
    }
  };

  return (
    <Router>
      <div className="app-container">
        <Header 
          onOpenSettings={() => setShowSettings(true)}
          onOpenPremium={() => setShowPremium(true)}
        />
        <main className="app-content">
          {renderScreen()}
        </main>
        {activeChatUser && (
          <ChatScreen 
            user={activeChatUser} 
            onClose={() => setActiveChatUser(null)} 
          />
        )}
        <TabBar activeTab={activeTab} onTabChange={setActiveTab} />

        {/* Settings Modal */}
        <Modal
          isOpen={showSettings}
          onClose={() => setShowSettings(false)}
          title={t('settings.title')}
        >
          <div className="settings-content">
            <div className="settings-section">
              <h3>{t('settings.account')}</h3>
              <div className="settings-item">
                <span>{t('settings.email')}</span>
                <span className="settings-value">user@example.com</span>
              </div>
              <div className="settings-item">
                <span>{t('settings.phone')}</span>
                <span className="settings-value">+1 234 567 8900</span>
              </div>
              <button className="btn btn-outline">{t('settings.changePassword')}</button>
            </div>

            <div className="settings-section">
              <h3>{t('settings.language')}</h3>
              <div className="language-selector">
                {availableLanguages.map(lang => (
                  <button
                    key={lang.code}
                    className={`language-option ${language === lang.code ? 'active' : ''}`}
                    onClick={() => changeLanguage(lang.code)}
                  >
                    <span className="lang-name">{lang.nativeName}</span>
                    {language === lang.code && <span className="checkmark">âœ“</span>}
                  </button>
                ))}
              </div>
            </div>

            <div className="settings-section">
              <h3>{t('settings.notifications')}</h3>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" defaultChecked />
                  <span>{t('settings.pushNotifications')}</span>
                </label>
              </div>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" defaultChecked />
                  <span>{t('settings.emailNotifications')}</span>
                </label>
              </div>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" />
                  <span>{t('settings.smsNotifications')}</span>
                </label>
              </div>
            </div>

            <div className="settings-section">
              <h3>{t('settings.privacy')}</h3>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" defaultChecked />
                  <span>{t('settings.showProfile')}</span>
                </label>
              </div>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" defaultChecked />
                  <span>{t('settings.allowMessages')}</span>
                </label>
              </div>
              <div className="settings-item">
                <label className="settings-toggle">
                  <input type="checkbox" />
                  <span>{t('settings.shareLocation')}</span>
                </label>
              </div>
            </div>

            <div className="settings-section">
              <h3>{t('settings.about')}</h3>
              <div className="settings-item">
                <span>{t('settings.version')}</span>
                <span className="settings-value">1.0.0</span>
              </div>
              <div className="settings-item">
                <a href="#" className="settings-link">{t('settings.termsOfService')}</a>
              </div>
              <div className="settings-item">
                <a href="#" className="settings-link">{t('settings.privacyPolicy')}</a>
              </div>
            </div>

            <div className="settings-actions">
              <button className="btn btn-outline">{t('settings.signOut')}</button>
              <button className="btn btn-danger">{t('settings.deleteAccount')}</button>
            </div>
          </div>
        </Modal>
      </div>
    </Router>
  );
}

export default App;
