import React, { useState } from 'react';
import { CloseIcon } from '../../utils/icons';

const AddEventModal = ({ artist, isOpen, onClose, onSave }) => {
  const [eventData, setEventData] = useState({
    venue: '',
    event: '',
    date: '',
    startTime: '',
    endTime: '',
    fee: '',
    currency: 'USD',
    capacity: '',
    status: 'pending',
    requirements: [],
    paymentTerms: '50% advance, 50% after performance',
    cancellationPolicy: '72 hours notice required',
    notes: ''
  });

  const [errors, setErrors] = useState({});

  const technicalRequirements = [
    'CDJ-3000s',
    'DJM-A9 Mixer',
    '4-channel mixer',
    'Monitor speakers',
    'Microphone',
    'Sound check 30 min',
    'Green room access',
    'Vinyl setup',
    'Lighting control'
  ];

  const handleInputChange = (field, value) => {
    setEventData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleRequirementToggle = (requirement) => {
    setEventData(prev => ({
      ...prev,
      requirements: prev.requirements.includes(requirement)
        ? prev.requirements.filter(req => req !== requirement)
        : [...prev.requirements, requirement]
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!eventData.venue.trim()) newErrors.venue = 'Venue is required';
    if (!eventData.event.trim()) newErrors.event = 'Event name is required';
    if (!eventData.date) newErrors.date = 'Date is required';
    if (!eventData.startTime) newErrors.startTime = 'Start time is required';
    if (!eventData.endTime) newErrors.endTime = 'End time is required';
    if (!eventData.fee || eventData.fee <= 0) newErrors.fee = 'Valid fee is required';

    // Check if end time is after start time
    if (eventData.startTime && eventData.endTime && eventData.startTime >= eventData.endTime) {
      newErrors.endTime = 'End time must be after start time';
    }

    // Check if date is in the future
    if (eventData.date && new Date(eventData.date) < new Date()) {
      newErrors.date = 'Event date must be in the future';
    }

    return newErrors;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formErrors = validateForm();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    // Create event object with additional metadata
    const newEvent = {
      id: Date.now(), // In real app, this would come from backend
      artistId: artist?.id,
      artistName: artist?.name,
      ...eventData,
      duration: calculateDuration(eventData.startTime, eventData.endTime),
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    onSave(newEvent);
    handleClose();
  };

  const calculateDuration = (start, end) => {
    const startDate = new Date(`2000-01-01T${start}`);
    const endDate = new Date(`2000-01-01T${end}`);
    const diffMs = endDate - startDate;
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMinutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));

    if (diffHours === 0) return `${diffMinutes} minutes`;
    if (diffMinutes === 0) return `${diffHours} hours`;
    return `${diffHours}h ${diffMinutes}m`;
  };

  const handleClose = () => {
    setEventData({
      venue: '',
      event: '',
      date: '',
      startTime: '',
      endTime: '',
      fee: '',
      currency: 'USD',
      capacity: '',
      status: 'pending',
      requirements: [],
      paymentTerms: '50% advance, 50% after performance',
      cancellationPolicy: '72 hours notice required',
      notes: ''
    });
    setErrors({});
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="add-event-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Add New Event - {artist?.name}</h2>
          <button className="modal-close-btn" onClick={handleClose}>
            <CloseIcon />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="add-event-form">
          {/* Basic Event Info */}
          <div className="form-section">
            <h3>Event Details</h3>

            <div className="form-row">
              <div className="form-group">
                <label>Venue *</label>
                <input
                  type="text"
                  value={eventData.venue}
                  onChange={(e) => handleInputChange('venue', e.target.value)}
                  placeholder="e.g. Womb Tokyo"
                  className={errors.venue ? 'error' : ''}
                />
                {errors.venue && <span className="error-text">{errors.venue}</span>}
              </div>

              <div className="form-group">
                <label>Event Name *</label>
                <input
                  type="text"
                  value={eventData.event}
                  onChange={(e) => handleInputChange('event', e.target.value)}
                  placeholder="e.g. New Year Countdown 2025"
                  className={errors.event ? 'error' : ''}
                />
                {errors.event && <span className="error-text">{errors.event}</span>}
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Date *</label>
                <input
                  type="date"
                  value={eventData.date}
                  onChange={(e) => handleInputChange('date', e.target.value)}
                  className={errors.date ? 'error' : ''}
                />
                {errors.date && <span className="error-text">{errors.date}</span>}
              </div>

              <div className="form-group">
                <label>Start Time *</label>
                <input
                  type="time"
                  value={eventData.startTime}
                  onChange={(e) => handleInputChange('startTime', e.target.value)}
                  className={errors.startTime ? 'error' : ''}
                />
                {errors.startTime && <span className="error-text">{errors.startTime}</span>}
              </div>

              <div className="form-group">
                <label>End Time *</label>
                <input
                  type="time"
                  value={eventData.endTime}
                  onChange={(e) => handleInputChange('endTime', e.target.value)}
                  className={errors.endTime ? 'error' : ''}
                />
                {errors.endTime && <span className="error-text">{errors.endTime}</span>}
              </div>
            </div>
          </div>

          {/* Financial Details */}
          <div className="form-section">
            <h3>Financial Terms</h3>

            <div className="form-row">
              <div className="form-group">
                <label>Performance Fee *</label>
                <div className="fee-input-group">
                  <select
                    value={eventData.currency}
                    onChange={(e) => handleInputChange('currency', e.target.value)}
                    className="currency-select"
                  >
                    <option value="USD">USD</option>
                    <option value="EUR">EUR</option>
                    <option value="GBP">GBP</option>
                    <option value="JPY">JPY</option>
                  </select>
                  <input
                    type="number"
                    value={eventData.fee}
                    onChange={(e) => handleInputChange('fee', e.target.value)}
                    placeholder="3500"
                    min="0"
                    className={errors.fee ? 'error' : ''}
                  />
                </div>
                {errors.fee && <span className="error-text">{errors.fee}</span>}
              </div>

              <div className="form-group">
                <label>Venue Capacity</label>
                <input
                  type="number"
                  value={eventData.capacity}
                  onChange={(e) => handleInputChange('capacity', e.target.value)}
                  placeholder="1000"
                  min="0"
                />
              </div>

              <div className="form-group">
                <label>Status</label>
                <select
                  value={eventData.status}
                  onChange={(e) => handleInputChange('status', e.target.value)}
                >
                  <option value="pending">Pending</option>
                  <option value="confirmed">Confirmed</option>
                  <option value="negotiating">Negotiating</option>
                  <option value="cancelled">Cancelled</option>
                </select>
              </div>
            </div>

            <div className="form-group">
              <label>Payment Terms</label>
              <input
                type="text"
                value={eventData.paymentTerms}
                onChange={(e) => handleInputChange('paymentTerms', e.target.value)}
                placeholder="50% advance, 50% after performance"
              />
            </div>
          </div>

          {/* Technical Requirements */}
          <div className="form-section">
            <h3>Technical Requirements</h3>
            <div className="requirements-grid">
              {technicalRequirements.map(requirement => (
                <label key={requirement} className="requirement-checkbox">
                  <input
                    type="checkbox"
                    checked={eventData.requirements.includes(requirement)}
                    onChange={() => handleRequirementToggle(requirement)}
                  />
                  <span>{requirement}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Additional Info */}
          <div className="form-section">
            <h3>Additional Information</h3>

            <div className="form-group">
              <label>Cancellation Policy</label>
              <input
                type="text"
                value={eventData.cancellationPolicy}
                onChange={(e) => handleInputChange('cancellationPolicy', e.target.value)}
                placeholder="72 hours notice required"
              />
            </div>

            <div className="form-group">
              <label>Notes</label>
              <textarea
                value={eventData.notes}
                onChange={(e) => handleInputChange('notes', e.target.value)}
                placeholder="Additional notes, special requirements, or comments..."
                rows={3}
              />
            </div>
          </div>

          {/* Form Actions */}
          <div className="form-actions">
            <button type="button" className="btn btn-secondary" onClick={handleClose}>
              Cancel
            </button>
            <button type="submit" className="btn btn-primary">
              Add Event
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEventModal;
