import React, { useState, useEffect } from 'react';
import { dummyProfiles, zones, countriesByZone, citiesByCountry, genresList } from '../../data/profiles';
import { HeartIcon } from '../../utils/icons';

const SearchScreen = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    role: '',
    zone: '',
    country: '',
    city: '',
    genre: ''
  });
  const [searchResults, setSearchResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);

  useEffect(() => {
    // Show all profiles initially
    setSearchResults(dummyProfiles);
  }, []);

  const handleSearch = () => {
    let results = dummyProfiles;

    // Filter by search query (name)
    if (searchQuery) {
      results = results.filter(profile => 
        profile.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by role
    if (filters.role) {
      results = results.filter(profile => profile.role === filters.role);
    }

    // Filter by zone
    if (filters.zone) {
      results = results.filter(profile => profile.zone === filters.zone);
    }

    // Filter by country
    if (filters.country) {
      results = results.filter(profile => profile.country === filters.country);
    }

    // Filter by city
    if (filters.city) {
      results = results.filter(profile => profile.city === filters.city);
    }

    // Filter by genre
    if (filters.genre) {
      results = results.filter(profile => 
        profile.genres && profile.genres.includes(filters.genre)
      );
    }

    setSearchResults(results);
    setHasSearched(true);
  };

  const handleFilterChange = (filterType, value) => {
    const newFilters = { ...filters, [filterType]: value };

    // Reset dependent filters when parent changes
    if (filterType === 'zone') {
      newFilters.country = '';
      newFilters.city = '';
    } else if (filterType === 'country') {
      newFilters.city = '';
      // Set zone based on country
      const zone = Object.entries(countriesByZone).find(([_, countries]) => 
        countries.includes(value)
      )?.[0] || '';
      newFilters.zone = zone;
    } else if (filterType === 'city' && value) {
      // Set country and zone based on city
      const country = Object.entries(citiesByCountry).find(([_, cities]) => 
        cities.includes(value)
      )?.[0] || '';
      const zone = Object.entries(countriesByZone).find(([_, countries]) => 
        countries.includes(country)
      )?.[0] || '';
      newFilters.country = country;
      newFilters.zone = zone;
    }

    setFilters(newFilters);
  };

  const clearFilters = () => {
    setFilters({
      role: '',
      zone: '',
      country: '',
      city: '',
      genre: ''
    });
  };

  const getAvailableCountries = () => {
    if (filters.zone) {
      return countriesByZone[filters.zone] || [];
    }
    return Object.values(countriesByZone).flat().sort();
  };

  const getAvailableCities = () => {
    if (filters.country) {
      return citiesByCountry[filters.country] || [];
    }
    return Object.values(citiesByCountry).flat().sort();
  };

  const activeFilterCount = Object.values(filters).filter(v => v).length;

  return (
    <div className="screen active search-screen">
      <div className="search-header">
        <h2>Search</h2>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by name..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            className="search-input"
          />
          <button 
            className="filter-toggle-btn"
            onClick={() => setShowFilters(!showFilters)}
          >
            <span className="filter-icon">⚙</span> Filters
            {activeFilterCount > 0 && (
              <span className="filter-count">{activeFilterCount}</span>
            )}
          </button>
        </div>

        {/* Filters Section */}
        {showFilters && (
          <div className="filters-section">
            <div className="filters-grid">
              {/* Role Filter */}
              <div className="filter-group">
                <label>Role</label>
                <select 
                  value={filters.role} 
                  onChange={(e) => handleFilterChange('role', e.target.value)}
                >
                  <option value="">All Roles</option>
                  <option value="ARTIST">Artist</option>
                  <option value="AGENT">Agent</option>
                  <option value="PROMOTER">Promoter</option>
                  <option value="VENUE">Venue</option>
                </select>
              </div>

              {/* Zone Filter */}
              <div className="filter-group">
                <label>Zone</label>
                <select 
                  value={filters.zone} 
                  onChange={(e) => handleFilterChange('zone', e.target.value)}
                >
                  <option value="">All Zones</option>
                  {zones.map(zone => (
                    <option key={zone} value={zone}>{zone}</option>
                  ))}
                </select>
              </div>

              {/* Country Filter */}
              <div className="filter-group">
                <label>Country</label>
                <select 
                  value={filters.country} 
                  onChange={(e) => handleFilterChange('country', e.target.value)}
                >
                  <option value="">All Countries</option>
                  {getAvailableCountries().map(country => (
                    <option key={country} value={country}>{country}</option>
                  ))}
                </select>
              </div>

              {/* City Filter */}
              <div className="filter-group">
                <label>City</label>
                <select 
                  value={filters.city} 
                  onChange={(e) => handleFilterChange('city', e.target.value)}
                >
                  <option value="">All Cities</option>
                  {getAvailableCities().map(city => (
                    <option key={city} value={city}>{city}</option>
                  ))}
                </select>
              </div>

              {/* Genre Filter */}
              <div className="filter-group">
                <label>Genre</label>
                <select 
                  value={filters.genre} 
                  onChange={(e) => handleFilterChange('genre', e.target.value)}
                >
                  <option value="">All Genres</option>
                  {genresList.map(genre => (
                    <option key={genre} value={genre}>{genre}</option>
                  ))}
                </select>
              </div>
            </div>

            <div className="filter-actions">
              <button className="btn btn-outline" onClick={clearFilters}>Clear Filters</button>
              <button className="btn btn-primary" onClick={handleSearch}>Apply Filters</button>
            </div>
          </div>
        )}
      </div>

      {/* Search Results */}
      <div className="search-results">
        {searchResults.length > 0 ? (
          searchResults.map(profile => (
            <div key={profile.id} className="search-result-card">
              <div className="result-avatar">
                <img src={profile.avatar} alt={profile.name} />
                {profile.isVerified && <span className="verified-badge">✓</span>}
              </div>
              <div className="result-info">
                <div className="result-header">
                  <h3>{profile.name}</h3>
                  <span className={`role-badge ${profile.role.toLowerCase()}`}>
                    {profile.role}
                  </span>
                </div>
                <p className="result-location">{profile.location}</p>
                {profile.genres && profile.genres.length > 0 && (
                  <div className="result-genres">
                    {profile.genres.slice(0, 3).map(genre => (
                      <span key={genre} className="genre-tag">{genre}</span>
                    ))}
                  </div>
                )}
              </div>
              <div className="result-actions">
                <button className="btn btn-outline btn-icon">
                  <HeartIcon />
                </button>
                <button className="btn btn-primary btn-connect">Connect</button>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-state">
            <p>{hasSearched ? 'No results found' : 'Start searching to find profiles'}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchScreen;
